        "RsMenu": function(schoolList) {
            var mobileSchools = '', additionalHeaders = "", additionalLinks = "";

            for(var i in schoolList) {
                if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                    mobileSchools += '1,' + schoolList[i].schoolName + ',' + schoolList[i].schoolUrl + ', _blank;';
                }
            }

            // ADD GLOBAL ICONS TO MENU
            var menuGlobalIcons = [
                ["School Meals", "http://www.schoolnutritionandfitness.com/index.php?sid=1703091755087669"],
                ["Aeries Portal", "https://aeriesnet.pittsburg.k12.ca.us/parentportal/LoginParent.aspx?page=default.aspx"],
                ["News", "https://www.pittsburg.k12.ca.us/Page/4252"],
                ["PUSD Calendars", "https://www.pittsburg.k12.ca.us/site/Default.aspx?PageID=1385"],
                ["Athletics", "https://www.pittsburg.k12.ca.us/PUSDAthletics"],
                ["Title IX", "https://www.pittsburg.k12.ca.us/Domain/1959"],
                ["Board Agenda", "https://www.boarddocs.com/ca/pittsburg/Board.nsf/Public"],
                ["Contact Us", "https://www.pittsburg.k12.ca.us/Page/5979"]
            ];
            var menuIconNum = ("8" == "8;7;6;5;4;3;2;1;0") ? 0 : "8";

            for(var i = 0; i < menuIconNum; i++) {
                var linkCheck = (menuGlobalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : menuGlobalIcons[i][1];
                additionalLinks += "2, " + menuGlobalIcons[i][0] + ", " + linkCheck + ", _self;";
            }

            if(menuIconNum > 0) {
                additionalHeaders = ";Popular Links";
            }

            $("body").csRsMenu({
                "breakPoint" : window.csTemplate.MobileMenuBreakpoint,
                "slideDirection" : "left-to-right",
                "primaryColor" : "#1A1A1A",
                "secondaryColor" : "#0C0C0C",
                "accentColor" : "#BABABA",
                "menuButtonParent" : "#gb-channel-list",
                "showMenuBtnText" : "yes",
                "menuBtnText" : "Menu",
                "showSchools" : "no",
                "showTranslate" : "no",
                "translateId" : "none",
                "showAccount" : "yes",
                "usePageListNavigation" : "yes",
                "extraMenuOptions"      : "Schools" + additionalHeaders, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
                "extraMenuItems"        : mobileSchools + additionalLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
                "siteID" : "4",
                "allLoaded" : function(){ }
            });
        },
