$(document).ready(function(){
	$(".ui-article").each(function(){   
    	if ($(this).children("*[id^='hidFileName']").val() == "") {
      		$(this).children(".ui-filelist-container").hide();		
      	}
        
        
	});
    
    $(".file-library li").each(function() {
    	$(".ui-filelist-container a img[src*='filetype-pdf.png']", this).attr("src", "https://www.pittsburg.k12.ca.us/Static//GlobalAssets/Images/Icons/16/icon-pdf.svg");
    });
});

