/**
* Function that tracks a click on an outbound link in Google Analytics.
* This function takes a valid URL string as an argument, and uses that URL string
* as the event label.
*/
var trackOutboundLink = function(pageUrl, sliderLink) {
	gaMaster(function() {
		  var trackers = gaMaster.getAll();
		  for (var i=0; i<trackers.length; ++i) {
		    var tracker = trackers[i];
		    tracker.send('event', pageUrl, sliderLink);
		  }
	});
	//ga('master-tracker.send', 'event', pageUrl, sliderLink);
	//ga('send', );
}

