SchoolBlocksApp.blocksReady(function () {
    var Weather = new SchoolBlocksApp.Weather("#weatherslider-f-0d60146ac4c22f-bde8d4-112-60cfe9ef"),
        commandUrl = '/block/8c46f2fa-2e16-40f4-9dbd-cee0df01c216/command',
