			["Connect", "@tahantostags"],
			["Connect", "@tahantostags"]
		];
		var menuIconNum = ("6" == "8;7;6;5;4;3;2;1;0") ? 4 : "6";

		for(var i = 0; i < menuIconNum; i++) {
			var linkCheck = (menuGlobalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : menuGlobalIcons[i][1];
			additionalLinks += "1, " + menuGlobalIcons[i][0] + ", " + linkCheck + ", _self;";
		}

		if(menuIconNum > 0) {
			additionalHeaders = "Popular Links;";
		}

		//SET UP HEADER LINKS FOR RS MENU
		var menuHeaderLinks = [
			["On-Line Payments Berlin", "https://unipaygold.unibank.com/Default.aspx?customerid=933","_blank"],
			["On-Line Payments Boylston", "https://unipaygold.unibank.com/Default.aspx?customerid=870","_blank"],
