        $(".sw-mystart-dropdown .sw-dropdown-list li a").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
					} else {
						// FOCUS NEXT ITEM

						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
				break;

                // CONSUME TAB KEY
				case csKeyCodes.tab:
					e.preventDefault();

                    $(this).parent().parent().parent().slideUp("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");
                    $(this).parent().parent().parent().parent().removeClass("open");
					$(this).parent().parent().parent().parent().next().focus();
				break;

			}
		});

        $("#sw-myaccount-list li a").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$(this).closest("#sw-myaccount-list").find("li:last-child > a").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(this).closest("#sw-myaccount-list").find("li:first-child > a").focus();
					} else {
						// FOCUS NEXT ITEM

						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(this).closest("#sw-myaccount-list").find("li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(this).closest("#sw-myaccount-list").find("li:last-child > a").focus();
				break;

                // CONSUME TAB KEY
				case csKeyCodes.tab:
					e.preventDefault();

                    $(this).parent().parent().slideUp("slow", "swing").attr("aria-hidden", ($("#sw-myaccount-list", this).attr("aria-hidden") == "true") ? "false" : "true");
                    $(this).parent().parent().parent().removeClass("open");
