body.script:
if ($('#back-to-top').length) {
    var scrollTrigger = 100, // px
        backToTop = function () {
            var scrollTop = $(window).scrollTop();
            if (scrollTop > scrollTrigger) {
                $('#back-to-top').addClass('show');
            } else {
                $('#back-to-top').removeClass('show');
            }
        };
    backToTop();
    $(window).on('scroll', function () {
        backToTop();
    });
    $('#back-to-top').on('click', function (e) {
        e.preventDefault();
        $('html,body').animate({
            scrollTop: 0
        }, 900);
    });
}

jQuery(document).ready(function(jQuery) {
	jQuery('.navigate').click(function() {
		jQuery('.nav div ul:first-child').slideToggle(250);
		return false;
	});
	if ((jQuery(window).width() > 767) || (jQuery(document).width() > 767)) {
		jQuery('nav.nav-sec li').mouseenter(function() {
			jQuery(this).children('ul').css('display', 'none').stop(true, true).slideToggle(250).css('display', 'block').children('ul').css('display', 'none');
		});
		jQuery('nav.nav-sec li').mouseleave(function() {
			jQuery(this).children('ul').stop(true, true).fadeOut(250).css('display', 'block');
		})
	} else {
		jQuery('nav.nav-sec li').each(function() {
			if (jQuery(this).children('ul').length)
				jQuery(this).append('');
		});
		jQuery('nav.nav-sec .drop-down-toggle').click(function() {
			jQuery(this).parent().children('ul').slideToggle(250);
		});
	}
});

