function showBudgetButton()
	
{
	$('#budget-container').removeClass('budget-minimized');

		$('#budget-link, #budget-ci').width(0).height(0).show().animate({
			   width: 150,
			   height: 150,
			   opacity: 1
			   }, {duration: 1000}
		 ); 

	$('#budget-mi').width(0).height(0).show().animate({
			   width: 150,
			   height: 93,
			   opacity: 1
			   }, {duration: 1000}
		 ); 
	
}


function hideBudgetButton()
	
{
	$('#budget-container').removeClass('budget-minimized');
			  $('#budget-link, #budget-mi, #budget-ci').animate({
				  width: 0,
				  height: 0,
				  opacity: 0.4
			  }, {
			  duration: 1000,
			  complete: function() {
			  $('#budget-link, #budget-mi, #budget-ci').hide();
			  $('#budget-container').addClass('budget-minimized');
		
			  } 
			}
		  ); 
	
}

