$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=LEBUx%2bS5QySPURJLr7KovQ%3d%3d&SessionID=b5c1715c-50c1-4d8c-b61a-2f2873e975af&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f19%2f2020+11%3a30%3a48+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.80.110';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
   } catch(err) { 
      // prevent site error for analytics
   }
