                               $('body.SurveyBodyStyle').find('input').each(function(){  
                                               var currentWidth = $(this).width();  
                                               if(currentWidth >= 400){ 
                                                                $(this).css('width','400px'); 
                                                }   
                              });  
                               $('body.SurveyBodyStyle').find('textarea').each(function(){ 
                                                $(this).css('width','400px');
                                 });
                                 $('body.SurveyBodyStyle').find('table').each(function(){ 
                                                var currentWidth = $(this).width();
                                                 if(currentWidth >= 400){ 
                                                                $(this).css('width','400px');
                                                                 $(this).width('400');
                                                 }
                                 });
                 });

