        "Slideshow": function() {
        	var _this = this;

            if($("#sw-content-container10 .ui-widget.app.multimedia-gallery").length) {
                var mmg = eval("multimediaGallery" + $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
                mmg.props.defaultGallery = false;

                $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                    "efficientLoad" : true,
                    "imageWidth" : 1500,
                    "imageHeight" : 700,
                    "mobileDescriptionContainer": [960, 768, 640, 480, 320], // [960, 768, 640, 480, 320]
                    "linkedElement" : ["title"], // ["image", "title", "overlay"]
                    "playPauseControl" : true,
                    "backNextControls" : true,
                    "transitionType" : "fade", // fade, slide, custom
                    "transitionSpeed" : 3,
                    "transitionDelay" : 5,
                    "fullScreenRotator" : false,
                    "fullScreenBreakpoints" : [], // NUMERICAL - [960, 768, 640, 480, 320]
                    "onTransitionStart" : function(props) {
                    	if(!$(".mmg-description-info", props.element).length) {
                            $(".mmg-description-outer .mmg-description-title", props.element).wrap('
');
                        	$(".mmg-description-outer .mmg-description-caption", props.element).appendTo( $(".mmg-description-info", props.element));
                        }
                    }, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                    "allLoaded" : function(props) {
                    	//Description 
                    	$(".mmg-description", props.element).wrap('
');
                        $(".mmg-description-outer .mmg-description-title", props.element).wrap('
');
                        $(".mmg-description-outer .mmg-description-caption", props.element).appendTo( $(".mmg-description-info", props.element));

                        //Controls
                     	$(".mmg-control", props.element).wrapAll('

');
                        $(".mmg-controls-outer", props.element).prependTo($(".mmg-description-outer", props.element));
                     } // props.element, props.mmgRecords
                });
            }

            if($("#sw-content-container5 .ui-widget.app.multimedia-gallery").length) {
                var mmg = eval("multimediaGallery" + $("#sw-content-container5 .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
                mmg.props.defaultGallery = false;

                $("#sw-content-container5 .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                    "efficientLoad" : true,
                    "imageWidth" : 563,
                    "imageHeight" : 563,
                    "mobileDescriptionContainer": [960, 768, 640, 480, 320], // [960, 768, 640, 480, 320]
                    "linkedElement" : ["image"], // ["image", "title", "overlay"]
                    "playPauseControl" : true,
                    "backNextControls" : true,
                    "autoRotate" : true,
                    "hoverPause" : true,
                    "transitionType" : "fade", // fade, slide, custom
                    "transitionSpeed" : 3,
                    "transitionDelay" : 5,
                    "onTransitionStart" : function(props) { }, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                    "allLoaded" : function(props) { 

                        $(".mmg-slides-outer", props.element).wrapInner('
');
                        $(".mmg-description-outer", props.element).appendTo($(".mmg-slides-inner", props.element));

                        _this.InteractiveGraphics();     

                        $(".mmg-control", props.element).wrapAll('
');
                        $(".mmg-controls", props.element).appendTo($(".mmg-slides-outer", props.element));

                        $(".cs-graphic-4").insertAfter($(".cs-graphic-0"));
                        $(".cs-graphic-1, .cs-graphic-3").insertAfter($(".mmg-slides-inner", props.element));
                  		$(".cs-graphic-2").appendTo($("#hp-interactive-graphics .mmg-slides-outer"));

                        $(".mmg-slides-inner", props.element).hover(function() {
                           $(".mmg-description-outer", props.element).css("opacity", 1);
                        }, function() {
                           $(".mmg-description-outer", props.element).css("opacity", 0);
                        });

                        $(".mmg-description-link", props.element).on("focus", function() {
                           $(".mmg-description-outer", props.element).css("opacity", 1);
                        }).on("blur", function() {
                           $(".mmg-description-outer", props.element).css("opacity", 0);
                        });
                    } // props.element, props.mmgRecords
                });
            }
        },

        "StreamingVideo": function() {
        	var _this = this;

        	if(false) {
            	//Variables
                var mobilePhoto = '/cms/lib/NY50000157/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg';
                $("html").addClass("cs-streaming-video");

                if($("#sw-content-container10 .ui-widget.app.multimedia-gallery").length) {
                	$("#sw-content-container10 > div:not(#cs-fullscreen-video-outer), #sw-content-container10 > .mmg-description-outer").hide().attr("aria-hidden", true);
                }

                if($.trim(mobilePhoto) == "" || mobilePhoto.indexOf("default-man.jpg") > -1) {
                	mobilePhoto = "/cms/lib/SW00000019/Centricity/Template/2313/subsite-mobile-photo.jpg";
                }

                var videoVendor = 'Vimeo';
                videoVendor = videoVendor.toLowerCase();

                $("#sw-content-container10.region.ui-hp").fullScreenRotator({
                    "videoSource" : videoVendor, // OPTIONS ARE: youtube, vimeo
                    "videoID": '239434695', // YouTube and Vimeo ids are set as default in the script
                    "fullScreenBreakpoints" : [960], // OPTIONS ARE [960, 768, 640, 480, 320]
                    "showControls" : true,
                    "mobileBackgroundPhoto" : mobilePhoto,
                    "mobileBackgroundPhotoBreakpoint" : 768, // OPTIONS ARE 768, 640, 480, 320
                    "onReady" : function(props) {                        
                    	// ADD POSITIONING STYLES
                        var dynStyleSheet = document.createElement('style');
                        if(dynStyleSheet) {
                            dynStyleSheet.setAttribute('type', 'text/css');
                            var head = document.getElementsByTagName('head')[0];

                            if(head) {
                                head.insertBefore(dynStyleSheet, head.childNodes[0]);
                            }

                            var dynStyles = 'iframe#cs-fullscreen-video {' +
                                                'top: calc(((100vw * .4) - (100vw * ' + (props.height / props.width) + ')) / 2);' +	
                                            '}';

                            var rules = document.createTextNode(dynStyles);

                            if(dynStyleSheet.styleSheet){ // IE
                                dynStyleSheet.styleSheet.cssText = dynStyles;
                            } else {
                                dynStyleSheet.appendChild(rules);
                            }
                        }

                        $("#sw-content-container10").css("height", "calc(100vh - 132px)")

                        var sbvStructure = "";

                        sbvStructure += '


';

                        if('Video Title' != "" || 'Video Caption' != "") {
                            sbvStructure += '
';

                            if('Video Title' != "") {
                                sbvStructure += '
Video Title';
                            }

                            if('Video Caption' != "") {
                                sbvStructure += '
Video Caption';
                            }

                            sbvStructure += '';
						}

                        if('Video Link' != "" && 'www.youtube.com' != "") {
                            sbvStructure += '
Video Link';
                        }

                        sbvStructure += '';

                        $("#cs-fullscreen-video-outer").append(sbvStructure);

                        $("#cs-fullscreen-video-outer .cs-fullscreen-video-buttons").wrapInner('
').addClass("hide768").prependTo($("#cs-fullscreen-video-outer .mmg-description-outer"));

                        // ADD ARIA LABELS TO BUTTONS
                        $("#hp-slideshow .cs-fullscreen-video-button.mute-button").attr("aria-label", "Mute Background Video");
                        $("#hp-slideshow .cs-fullscreen-video-button.unmute-button").attr("aria-label", "Unmute Background Video");
                        $("#hp-slideshow .cs-fullscreen-video-button.play-button").attr("aria-label", "Play Background Video");
                        $("#hp-slideshow .cs-fullscreen-video-button.pause-button").attr("aria-label", "Pause Background Video");

                        if($(".w-1024").length && $(".cs-fullscreen-video-buttons.video-playing").length) {
                        	$(".cs-fullscreen-video-button.pause-button").click();

                            setTimeout(function(){ $(".cs-fullscreen-video-button.play-button").click(); }, 100);
                        }

                        // BUTTON ACTIONS
                        $("#hp-slideshow").on("click keydown", ".cs-fullscreen-video-button.play-button", function(e) {
                        	if(_this.AllyClick(e)) {
                            	setTimeout(function() {
                                	$("#hp-slideshow .cs-fullscreen-video-button.pause-button").focus();
                                }, 100);
                            }
                        });
                        $("#hp-slideshow").on("click keydown", ".cs-fullscreen-video-button.pause-button", function(e) {
                        	if(_this.AllyClick(e)) {
                            	setTimeout(function() {
                                	$("#hp-slideshow .cs-fullscreen-video-button.play-button").focus();
                                }, 100);
                            }
                        });
                        $("#hp-slideshow").on("click keydown", ".cs-fullscreen-video-button.mute-button", function(e) {
                        	if(_this.AllyClick(e)) {
                            	setTimeout(function() {
                                	$("#hp-slideshow .cs-fullscreen-video-button.unmute-button").focus();
                                }, 100);
                            }
                        });
                        $("#hp-slideshow").on("click keydown", ".cs-fullscreen-video-button.unmute-button", function(e) {
                        	if(_this.AllyClick(e)) {
                            	setTimeout(function() {
                                	$("#hp-slideshow .cs-fullscreen-video-button.mute-button").focus();
                                }, 100);
                            }
                        });
                    },
                    "onStateChange" : function(props) { }
                });

                // MOVE BUTTONS
                $("#hp-slideshow #cs-fullscreen-video-outer").append($("#hp-slideshow .cs-fullscreen-video-buttons"));
            }
        },

