                  	 $(".cs-graphic-item", this).addClass("hover");
                     $(".cs-graphic-caption", this).fadeIn(250);
                  }, function() {
                  	 $(".cs-graphic-item", this).removeClass("hover");
                     $(".cs-graphic-caption", this).fadeOut(250);
                  });

                  $(".cs-graphic-item").on("focus", function() {
                  	 $(this).addClass("hover");
                     $(this).parent().find(".cs-graphic-caption").fadeIn(250);
                  }).on("blur", function() {
                  	 $(this).removeClass("hover");
                     $(this).parent().find(".cs-graphic-caption").fadeOut(250);
                  });
              }
