        "Search": function() {
        	// FOR SCOPE
            var _this = this;

            // OPEN SEARCH DIALOG
            $(document).on("click keydown", ".gb-search-btn", function(e) {
                if(_this.AllyClick(e)) {
                	e.preventDefault();

                    if($(this).attr("aria-expanded") == "false") {
                    	$(this).attr({
                        	"aria-expanded": "true"
                        });
                        $("#gb-search-outer").attr("aria-hidden", "false").slideDown(250, "linear");
                        $("#gb-page").addClass("search-open");

                        setTimeout(function() {
                            $("#gb-search-input").focus();
                        }, 260);
                    } else {
                    	$(this).attr({
                        	"aria-expanded": "false"
                        });
                        $("#gb-search-outer").attr("aria-hidden", "true").slideUp(250, "linear");
                        $("#gb-page").removeClass("search-open");
                    }
                }
            });

			$("#gb-search-form").submit(function(e){
				e.preventDefault();
				if($("#gb-search-input").val() != "I'm looking for..." && $("#gb-search-input").val() != "") {
					window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }
			});

            $(document).on("keydown", "#gb-search-submit-btn", function(e) {
                if(e.shiftKey && e.keyCode == _this.KeyCodes.tab) {

                } else if(e.keyCode == _this.KeyCodes.tab) {
                	e.preventDefault();
                    $(".gb-search-btn").focus();
                }
            });

			$("#gb-search-input").focus(function() {
				if($(this).val() == "I'm looking for...") {
					$(this).val("");
				}
			}).blur(function() {
				if($(this).val() == "") {
					$(this).val("I'm looking for...");
				}
			});
        },

        /* General Methods */
        "AllyClick": function(event) {
            if(event.type == "click") {
            	event.preventDefault();
                return true;
            } else if(event.type == "keypress" || event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
            	event.preventDefault();
                return true;
            } else {
                return false;
            }
        },

        "CutHex": function(hex){
        	return (hex.charAt(0)=="#") ? hex.substring(1,7):hex;
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

        "HexToRgb": function(hex){
        	var r = parseInt((this.CutHex(hex)).substring(0,2),16);
	        var g = parseInt((this.CutHex(hex)).substring(2,4),16);
	        var b = parseInt((this.CutHex(hex)).substring(4,6),16);
	        return r+', '+g+', '+b;
        },

        "LoadImage": function(element, image, defaultImage, toggle) {
        	//Variables
            image = $.trim(image);

            if(toggle) {
            	var srcCheck = image.indexOf("Faces/default-man.jpg") > -1;

                if((image != "") && (!srcCheck)) {
                    $(element).append('');
                } else {
                    $(element).append('');
                }
            } else {
            	$(element).hide();
            }
        },

        "LoadIcon": function(icon, iconAlt) {
        	//Variables
            var iconImage = $.trim(icon);
            var iconType = "system";
            var splitSelection = iconImage.split("/");
            var iconCat = $.trim(splitSelection[0]);
            iconCat = iconCat.toLowerCase().replace(/\s+/g, "-"); // LOWERCASE THE CATEGORY NAME AND REPLACE ALL SPACES WITH DASHES
            var iconChoice = splitSelection[1].split(".");
            iconChoice = iconChoice[0];

            // DETERMINE IF THE ICON IS A SYSTEM ICON OR CUSTOM IMAGE
            if(iconChoice == "cms") {
            	var iconType = "image";
            }

            if(iconType == "image") {
                var structure = '';
            } else {
                var structure = '';
            }

            return structure;
        }
    };


