$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=QnI3kSCKtozNVtY8THL1xg%3d%3d&SessionID=708eb048-b55d-43fb-9a82-7e47960d0fe2&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f20%2f2020+1%3a01%3a36+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.84.130';
   try {
