			'iconColor'	: '#000000', // ICON COLOR
			'icon1'		: ['Communication/18.png', 'E-Mail ', 'https://mail.google.com/a/ccs.edu', '_self'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon2'		: ['Classroom/7.png', 'SchoolTool', 'https://schooltool.ccs.edu/schooltoolweb/', '_self'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon3'		: ['Site Utility/4.png', 'Directory', 'https://www.ccs.edu/domain/87', '_self'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon4'		: ['Site Utility/2.png', 'Calendar', 'https://www.ccs.edu/Page/2', '_self'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon5'		: [], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'siteID'	: '4',
			'siteAlias'	: 'clintoncentral',
			'calendarLink'	: '/Page/2',
			'contactEmail'	: 'webmaster@ccs.edu',
			'onComplete'	: function(){ }
		});
	}	
	function useChannelForSection(){
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }
	function dynamicContent(){
		//DOC fixes search box not allowing spaces
		$("#sw-mystart-search").unbind();

		$("#swsignin-txt-username").focus();

		$(".sw-myaccount-chevron").text("");
		$(".sw-dropdown-arrow").text("");
	        $("#sw-mystart-search #sw-search-button").text("search");

		var breadcrumbs = 'false';
		if(breadcrumbs == "false"){
			$(".sp-breadcrumbs").hide();
		}
		var showLogo = 'true'; 
		if(showLogo == "true") {
			$("#gb-logo").html('');
		} else {
			$("#gb-logo").hide();
		}

		//SHOW/HIDE SCHOOL NAME
        	var nameToggle = 'true';
		if(nameToggle == "false"){
			$("#gb-name").hide();
		}
		//SHOW/HIDE SCHOOL TAGLINE
		var taglineToggle = 'true';
		if(taglineToggle == "false"){
			$("#gb-tagline").hide();
		}

		if(nameToggle == "true" || taglineToggle == "true") {
			$("#gb-logo").css("max-width","200px");
		} else {
			$("#gb-logo").css("max-width","530px");
		}
	}
	function sameHeight(selector){
		if($("#hp-content").size()){
			var contentHeight = $("#hp-content").outerHeight();

			$(".gb-content-fade").height(contentHeight);

			selector.children().resize(function(){
				contentHeight = '';

				var contentHeight = $("#hp-content").outerHeight();

				$(".gb-content-fade").height(contentHeight);

			});
		}
		if($("#sp-content").size()){
			var contentHeight = $("#sp-content").outerHeight();

			$(".gb-content-fade").height(contentHeight);

			selector.children().resize(function(){
				contentHeight = '';

				var contentHeight = $("#sp-content").outerHeight();

				$(".gb-content-fade").height(contentHeight);

			});	
		}

		var channelHeight = $("#sw-channel-list-container").outerHeight();

		$("#gb-channel-list-outer").height(channelHeight);

		if(channelHeight > 35){
			$("#sw-channel-list-container").css("background", "url(/cms/lib/NY01913591/Centricity/Template/8/gb-channelbar-bg.png) repeat-x 0px 35px");
		}
	}
	function calendarListView() {
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}
