    $(function() {
        CreativeTemplate.Init(); //tip of the iceberg
    });

    var csTemplate = {};

    var CreativeTemplate = {

        /*****
            PROPERTIES
        *****/

        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "DefaultLogoSrc": "",
        "DynamicStyles": "", //This can be added to and if it's not empty it'll be added to the page in a stylesheet
        "MobileMenuBreakpoint": 768,



        /*****
            METHODS
        *****/

// BIG BANG METHOD
        "Init": function() {
            // FOR SCOPE
            window.csTemplate = this;

            this.SetTemplateProps();
            this.JsMediaQueries();
            this.GlobalIcons();
            this.SocialIcons();
            this.MyStart();
            this.Search();
            this.Header();
            this.ChannelBar();
            this.Body();
            this.Slideshow(); //move this into JsMediaQueries if it isn't present across all of the Breakpoints
            this.StreamingVideo();
            this.TabbedEvents();
            this.ArticleSlider();
            this.Footer();
            this.BuildStylesheet();
            this.AppAccordion();
            this.RsMenu();

            $(window).load(function(){ window.csTemplate.WindowLoad(); });
            $(window).resize(function(){ 
                 window.csTemplate.WindowResize(); 
                 window.csTemplate.CalendarListView();
            });
            $(window).scroll(function() { window.csTemplate.WindowScroll(); });

            // HANDLE LINKS THAT OPEN IN A NEW WINDOW
			csGlobalJs.OpenInNewWindowWarning();

            if($("#dashboard-sidebar").length){
            	$("#gb-page").attr("data-dashboard","true");
            }
        },


// TOP LEVEL (Window/Document) EVENT HANDLERS
        "WindowLoad": function() {
            if($(window).width() >= 320 && $(window).width() <= 639){
                this.CalendarListView();
            }    

            $("div.ui-widget.app.navigation li div.bullet").click(function(){
                return true;
            });

            // HANDLE LINKS THAT OPEN IN A NEW WINDOW
			csGlobalJs.OpenInNewWindowWarning();

            this.MagicLine();

        },
        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
            //this.StickyChannelBar();
        },
        "WindowScroll": function() {
        	this.StickyChannelBar();

            /*var slideshowTop = $(".hp #hp-slideshow-outer").offset().top;
            var slideshowBottom = slideshowTop + $("#hp-slideshow-outer").outerHeight(true);
            var scrollTop = $(window).scrollTop();
            var scrollBottom = scrollTop + $(window).height();*/

            /*if(scrollBottom <= slideshowBottom) {
                $("#hp-slideshow-outer").removeClass("unfixed");
            } else {
                $("#hp-slideshow-outer").addClass("unfixed");
            }*/

            $.fn.isInViewport = function() {
                  var elementTop = $("#hp-slideshow-outer").offset().top;
                  var elementBottom = elementTop + $("#hp-slideshow-outer").outerHeight();
                  console.log("element top: " + elementTop);
                  console.log("element bottom: " + elementBottom);

                  var viewportTop = $(window).scrollTop();
                  var viewportBottom = viewportTop + $(window).height();
                  console.log("viewport top: " + viewportTop);
                  console.log("viewport bottom: " + viewportBottom);

                  //return elementBottom > viewportTop && elementTop < viewportBottom;
                  return elementTop < viewportTop && elementBottom < viewportBottom

                };

                if ($(this).isInViewport()) {
                  $("#hp-slideshow-outer").addClass("unfixed");
                } else {
                  $("#hp-slideshow-outer").removeClass("unfixed");
                }
        },

//TEMPLATE EVENT HANDLERS
        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
                	var channelHeight = $("#gb-channel-list-outer").height();
                    var headerHeight = $("#gb-header-outer").height() + 26;
                    var logoOffset = channelHeight - 12;
                    var districtLogoOffset = channelHeight - 10;
                    var giOffset = channelHeight + headerHeight;
                    $("#gb-logo").css("top",logoOffset + "px");
                    $(".eicsd #gb-logo").css("top",districtLogoOffset + "px");
                    $("#gb-header-outer").css("top",channelHeight + "px");
                    $("#hp-slideshow-outer").css("margin-top", giOffset + "px");
                    //$("#gb-global-icons-outer").css("top",giOffset + "px");
                    this.StickyChannelBar();
                break;
                case "768":

                break;
                case "640":

                break;
                case "480":

                break;
                case "320":

                break;
            }
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

// INFORMATION SETTERS
        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },
        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

          // 
        "BuildStylesheet": function() {

            var primaryColor80 = "rgba(" + this.HexToRgb('#13294b') + ", .8)";
            var globalIconBackgroundColor80 = "rgba(" + this.HexToRgb('#FFFFFF') + ", .8)";
            var mmgBackgroundColor80 = "rgba(" + this.HexToRgb('#ffffff') + ", .8)";
            var regCBackgroundColor70 = "rgba(" + this.HexToRgb('#13294b') + ", .7)";
            var spGlobalIconBackgroundColor80 = "rgba(" + this.HexToRgb('#C1C6C8') + ", .8)";
            var secondaryTextColor80 = "rgba(" + this.HexToRgb('#13294b') + ", .8)";


            var dynStyleSheet = document.createElement('style');

            if(dynStyleSheet) {
                dynStyleSheet.setAttribute('type', 'text/css');
                var head = document.getElementsByTagName('head')[0];

                if(head) {
                    head.insertBefore(dynStyleSheet, head.childNodes[0]);
                }

                var dynStyles ='	.sp #gb-global-icons-outer {' +
                    '		background: ' + spGlobalIconBackgroundColor80 + ';' +
                    '}' +
                    '	.sp .cs-global-icons li {' +
                    '		border-bottom: 1px solid ' + spGlobalIconBackgroundColor80 + ';' +
                    '}' +
                    '@media (max-width: 1023px) {' +
						'.icon-font-scroll:before {' +
							'		color: ' + secondaryTextColor80 + ';' +
							'}' +
						'}' +
                    '}';



                var rules = document.createTextNode(dynStyles);

                if(dynStyleSheet.styleSheet){ // IE
                    dynStyleSheet.styleSheet.cssText = this.DynamicStyles;
                } else {
                    dynStyleSheet.appendChild(rules);
                }
            }
        },
        "HexToRgb": function(hex){
            var r = parseInt((this.CutHex(hex)).substring(0,2),16);
            var g = parseInt((this.CutHex(hex)).substring(2,4),16);
            var b = parseInt((this.CutHex(hex)).substring(4,6),16);
            return r+', '+g+', '+b;
        },
        "CutHex": function(hex){
            return (hex.charAt(0)=="#") ? hex.substring(1,7):hex;
        },


//TEMPLATE METHODS
        "MyStart": function() {

        	var template = this;

            //TO FIX THE ISSUE OF SEARCH NOT ALLOWING SPACES
            $("#sw-mystart-search").unbind();

            //APPEND SEARCH TO GB-SEARCH-BOTTOM
            $("#sw-mystart-search").appendTo("#gb-search-bottom");

            //ADD ROLE AND ARIA LABEL TO SEARCH BUTTON
            $("#sw-search-button").attr("role","button").attr("aria-label","Submit Search");

            //ANIMATE SEARCH CONTAINER
            $("#gb-search-btn").on("click keydown", function(e) {
            	searchHoverTimer = "";
                if(template.AllyClick(e)) {
                    e.preventDefault();
                    if($("#gb-search-container").hasClass("open")){
                    $(this).attr("aria-expanded","false").focus();
                    clearTimeout(searchHoverTimer);
                        $("#gb-search-container").removeClass("open").attr("aria-hidden","true");
                            $("#gb-search-container #sw-search-input").attr("tabindex","-1");

                    } else {
                    	$(this).attr("aria-expanded","true");
                        $("#gb-search-container").addClass("open").attr("aria-hidden","false");
                        searchHoverTimer = setTimeout(function(){
                        	$("#gb-search-container #sw-search-input").focus().attr("tabindex","0");

                        }, 1000);

                    }
                }
            });  

            //REPLACE MYSTART DROPDOWN ARROWS
            $(".sw-myaccount-chevron").text("");
            $(".sw-dropdown-arrow").text("");

            //ADD TRANSITION CLASS TO ALL MYSTART DROPDOWNS
            $(".sw-mystart-dropdown").addClass("transition");

            //CHANGE THE SCHOOLLIST TEXT
            $(".sw-mystart-dropdown.schoollist .selector").text("Our Schools");

            //ADD TRANSITION CLASS TO ALL MYSTART BUTTONS
            $(".sw-mystart-button").addClass("transition");

