        "Header": function() {
            //ADD LOGO
            var logoCheck = "true";
            var logoTwoCheck = "true";
            var logoSrc = jQuery.trim("/cms/lib/NY02214279/Centricity/Template/GlobalAssets/images///logo/New district logo.png");
            var logoTwoSrc = jQuery.trim("/cms/lib/NY02214279/Centricity/Template/GlobalAssets/images///logos_2019/EICSD_logo_two.png");
            if(logoCheck == "true") {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append('').removeClass("hidden");
                } else {
                    $("#gb-logo").append('').removeClass("hidden");
                }
            }    
            if(logoTwoCheck == "true") {
                var srcTwoSplit = logoTwoSrc.split("/");
                var srcTwoSplitLen = srcTwoSplit.length;
                if((logoTwoSrc != "") && (srcTwoSplit[srcTwoSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append('');
                } else {
                    $("#gb-logo").append('');
                }

            }

            //ADD SITENAME IF SECOND PART OF LOGO IS NOT ACTIVE
            var siteNameOne = jQuery.trim('East Irondequoit Central School District');
            var siteNameTwo = jQuery.trim('');

            if((siteNameOne == "") && (siteNameTwo == "")) {
                var splitLen = 2;
                var siteName = "East Irondequoit Central School District";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
                $("#gb-sitename").prepend("
" + siteNameBegin + "" + siteNameEnd + "</span");
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "");
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "" + siteNameTwo + "");
            }
            if(logoTwoCheck != "true"){
            	$("#gb-sitename").addClass("one-logo").removeClass("hidden");
            } else {
            	$("#gb-sitename").addClass("double-logo");
                $("#gb-header-outer").addClass("double-logo");
            }
        },
        "ChannelBar": function() {
            //USE CHANNEL NAME FOR PAGE LIST NAVIGATION HEADER IF ONE NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
                $("div.sp.column.one .ui-widget-header").append("
");
