            //ADD FIRST CLASS TO LAST CHANNEL ITEM
            $("li.sw-channel-item").first().addClass("first");
            //ADD LAST CLASS TO LAST CHANNEL ITEM
            $("li.sw-channel-item").last().addClass("last");

            //ADD FIRST CLASS TO LAST CHANNEL ITEM
            $(".hp li.sw-channel-item").first().addClass("active");

            $("#gb-channel-list #sw-channel-list-container li.sw-channel-item").each(function(index){

                // LOWERCASE THE CHANNEL NAME
                var channelText = $("> a span", this).text().toLowerCase();
                // SET HOME AND CALENDAR CLASSES ON HOME AND CALENDAR CHANNELS
                if(channelText == "home") {
                    $(this).addClass("home");
                } else if(channelText == "calendar") {
                    $(this).addClass("calendar");
                }

            });

            //Overwrite system hover functionality for an animated version.
            $('#channel-navigation > .sw-channel-item > a').off('touchstart');
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){ //in
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){ //out
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            //ADD TRANSITION CLASS TO ALL CHANNEL ITEMS
            $("ul.sw-channel-list li.sw-channel-item").addClass("transition");

            //CHANGE TEXT TO HOME ICON FOR HOME CHANNEL
            $("li.sw-channel-item.home > a").html('');

            var channelHeight = $("#gb-channel-list-outer").outerHeight(true);
            var headerHeight = $("#gb-header-outer").outerHeight(true);
            var contentOffset = channelHeight + headerHeight;
            $(".sp main").css("margin-top", contentOffset);
            $(window).resize(function(){
            	var channelHeight = $("#gb-channel-list-outer").outerHeight(true);
                var headerHeight = $("#gb-header-outer").outerHeight(true);
                var contentOffset = channelHeight + headerHeight;
                $(".sp main").css("margin-top", contentOffset);
            });

        },
        "MagicLine": function() {
        	//MAGIC LINE
            var $el, leftPos, newWidth,
            $mainNav = $("#channel-navigation");

            $mainNav.append("
");
            var $magicLine = $("#magic-line");

            $magicLine
                .width($("li.sw-channel-item.active").width())
                .css("left", $("li.sw-channel-item.active").position().left)
                .data("origLeft", $magicLine.position().left)
                .data("origWidth", $magicLine.width());

            $("li.sw-channel-item").hover(function() {
                $el = $(this);
                leftPos = $el.position().left;
                newWidth = $el.width();
                $magicLine.stop().animate({
                    left: leftPos,
                    width: newWidth
                });
            }, function() {
                $magicLine.stop().animate({
                    left: $magicLine.data("origLeft"),
                    width: $magicLine.data("origWidth")
                });    
            });
        },


        "StickyChannelBar": function() {
        	var channelHeight = $("#gb-channel-list-outer").outerHeight(true);
            var headerHeight = $("#gb-header-outer").outerHeight(true);
            var contentOffset = channelHeight + headerHeight;

            var headerOffSet = $("header").offset().top;

            if ($(window).scrollTop() <= headerHeight) {
                if($(".spn").length || $(".sp").length && $(".ui-widget.app.calendar").length) {
                    $(".wcm-controls").css("margin-top", "0px");
                }
            } else {
                if($(".spn").length || $(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                    	//$(".wcm-controls").css("top", $("#gb-channel-list-outer").outerHeight(true));
                        $(".wcm-controls").css("top", contentOffset);
                    }
                }
            }

            var scrollTop = $(document).scrollTop();
            if(!$(".lt-1024").length){
            	//$(".sp main").css("margin-top", contentOffset);
                if(scrollTop >= 120){
                    $("#gb-page").addClass("logo-gone");
                    $("#gb-mystart .sw-mystart-dropdown.translate .selector").attr("tabindex","0");
                    $("#gb-sitename").removeClass("hidden");
                    $("#gb-global-icons-outer .cs-mystart-dropdown.translate").prependTo(".gb-mystart");
                    //$("#hp-slideshow-outer").addClass("unfixed");
                } else {
                    $("#gb-page").removeClass("logo-gone");
                    $("#gb-mystart .sw-mystart-dropdown.translate .selector").attr("tabindex","-1");
                    if($("#gb-sitename").hasClass("double-logo")){
                    	$("#gb-sitename").addClass("hidden");	
                    }
                    $(".gb-mystart .cs-mystart-dropdown.translate").appendTo("#gb-global-icons-outer .cs-global-icons li:last-child");
                    //$("#hp-slideshow-outer").removeClass("unfixed");
                }
            }

            /*$("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("header").outerHeight(true) + "px"
                        });*/
        },

