        "Search": function() {
            var _this = this;

            $(document).on("click keydown", "#gb-search-toggle", function(e){
                if(_this.AllyClick(e)) {
                	e.preventDefault();

                    // SEARCH IS CURRENTLY CLOSED
                    if($("#gb-search").attr("aria-hidden") == "true") {
                    	$("#gb-search-toggle").attr("aria-expanded", "true");
                    	$("#gb-search").attr("aria-hidden", "false");
                        $("#gb-search-input").attr("tabindex","0");
                        $("#sw-go-button").attr("tabindex","0");
                        setTimeout(function() {
                        	$("#gb-search-input").focus();
                        }, 500);
                    }

                    // SEARCH IS CURRENTLY OPEN
                    else {
                    	$("#gb-search-toggle").attr("aria-expanded", "false");
                    	$("#gb-search").attr("aria-hidden", "true");
                        $("#gb-search-input").attr("tabindex","-1");
                        $("#sw-go-button").attr("tabindex","-1");
                    }
                }
            });

            /*$("#gb-search-input").blur(function() {
            	$("#gb-search-toggle").attr("aria-expanded", "false");
            	$("#gb-search").attr("aria-hidden", "true");
            });*/

            $("#gb-search-form").submit(function(e){
                e.preventDefault();

                if($.trim($("#gb-search-input").val()) != "" && $.trim($("#gb-search-input").val()) != "I'm Looking for...") {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }
            });

            $("#sw-go-button").on("keydown", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {

                    // CONSUME TAB KEY
                    case _this.KeyCodes.tab:
                    	$("#gb-search").attr("aria-hidden", "true");
                        $("#gb-search-input").attr("tabindex","-1");
                        $("#sw-go-button").attr("tabindex","-1");
                        $("#gb-search-toggle").attr("aria-expanded", "false").focus();
                    break;

                }
            });

            $("#gb-search-input").on("keydown", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {

                    // CONSUME TAB KEY
                    case _this.KeyCodes.tab:
                    	if(e.shiftKey) {
                        	e.preventDefault();
                            $("#gb-search").attr("aria-hidden", "true");
                            $("#gb-search-input").attr("tabindex","-1");
                            $("#sw-go-button").attr("tabindex","-1");
                            $("#gb-search-toggle").attr("aria-expanded", "false").focus();
                        }
                    break;

                }
            });
        },


