        "Slideshow": function() {
            if($("#sw-content-container10 .ui-widget.app.multimedia-gallery").length) {
                var mmg = eval("multimediaGallery" + $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
                mmg.props.defaultGallery = false;

                $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                    "efficientLoad" : true,
                    "imageWidth" : 1500,
                    "imageHeight" : 750,
                    "mobileDescriptionContainer": [768, 640, 480, 320], // [960, 768, 640, 480, 320]
                    "galleryOverlay" : false,
                    "linkedElement" : [], // ["image", "title", "overlay"]
                    "playPauseControl" : true,
                    "backNextControls" : true,
                    "bullets" : false,
                    "thumbnails" : false,
                    "thumbnailViewerNum": [4, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                    "autoRotate" : false,
                    "hoverPause" : true,
                    "transitionType" : "fade", // fade, slide, custom
                    "transitionSpeed" : 1.5,
                    "transitionDelay" : 4,
                    "fullScreenRotator" : false,
                    "fullScreenBreakpoints" : [], // NUMERICAL - [960, 768, 640, 480, 320]
                    "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                    "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                    "onTransitionStart" : function(props) {
                    	if($(".mmg-slide.next .mmg-description",props.element).is(":empty")){
                        	$("#hp-slideshow .mmg-container .mmg-control-outer").css("left", "30px");
                        } else {
                        	$("#hp-slideshow .mmg-container .mmg-control-outer").css("left", "calc(28% + 86px)");
                        }
                    }, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                    "onTransitionEnd" : function(props) {

                    }, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                    "allLoaded" : function(props) {
                    	$("#hp-slideshow .mmg-container .mmg-control").not(".play-pause").addClass("icon-font-arrow");
                    	$("#hp-slideshow .mmg-container .mmg-control").wrapAll('
');
                        $("#hp-slideshow .mmg-container .mmg-control.play-pause").insertAfter("#hp-slideshow .mmg-container .mmg-control.back");

                    }, // props.element, props.mmgRecords
                    "onWindowResize": function(props) {} // props.element, props.mmgRecords
                });
            }
        },

        "StreamingVideo": function() {
        	if("false" == "true") {

                $('#sw-content-container10 > div[id*="pmi"]').hide();

                var mobilePhoto = "/cms/lib/NY02214279/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg";

                if($.trim(mobilePhoto) == "" || mobilePhoto.indexOf("default-man.jpg") > -1) {
                	mobilePhoto = "/cms/lib/NY02214279/Centricity/Template/1036/placeholders/streaming-video-default.jpg";
                }

                $("#sw-content-container10.region.ui-hp").fullScreenRotator({
                    "videoSource" : "youtube", // OPTIONS ARE: youtube, vimeo
                    "videoID": "X9hm-Iq_mKw", // YouTube and Vimeo ids are set as default in the script
                    "fullScreenBreakpoints" : [], // OPTIONS ARE [960, 768, 640, 480, 320]
                    "showControls" : true,
                    "showProgressBar" : false, // CURRENTLY NOT IN USE
                    "progressBarType" : "line", // CURRENTLY NOT IN USE BUT OPTIONS ARE: line, circle
                    "progressBarCircleWidth": 70, // pixels
                    "progressBarCircleHeight": 70, // pixels
                    "progressBarStrokeWidth": 10, // pixels
                    "progressBarStrokeColor": "#FFF",
                    "showMobileBackgroundPhoto" : false,
                    "mobileBackgroundPhoto" : mobilePhoto,
                    "mobileBackgroundPhotoBreakpoint" : 640, // OPTIONS ARE 768, 640, 480, 320
                    "useCustomResize": true,
                    "extendResize": function(element, videoHeight, videoWidth){
                    	var viewWidth = $(element).width();
                        var viewRatio = videoHeight / videoWidth;
                        var viewHeight = viewWidth * viewRatio;
                        var sliderWidth = viewWidth;

                        $('#cs-fullscreen-video-outer').css({"width": sliderWidth + "px", "height": viewHeight + "px"});
                        $('#cs-fullscreen-video').css({"width": viewWidth + "px", "height": viewHeight + "px"});
                    },
                    "onReady" : function() { 
                    	//add our custom controls
                    	//$('#hp-slideshow .cs-fullscreen-video-buttons').append('');
                    	//$('#hp-slideshow .cs-fullscreen-video-buttons').append('');




                        // add the description if there's info for it.
                        if('Welcome to East Irondequoit CSD' != '' || '' != '' || 'https://youtu.be/X9hm-Iq_mKw' != ''){                             
                        	var streamingDesc = '


';

