	function equalHeight() {
		// #hp-content -- ADDS THE HEIGHT TO COLUMN ONE, COLUMN TWO AND COLUMN THREE
		var hpHeight = $("div#hp-content").height();
		$("div.hp.column.one, div.hp.column.two").height(hpHeight); 	
		$("div.hp.column.three").height(hpHeight+140); 

		var spHeight = 	$("div#sp-content").height();
		$("div.sp.column").height(spHeight); 

		$("#hp-content div.ui-widget.app, #sp-content div.ui-widget.app").resize(function(){
			$("div.sp.column").height(""); 
			$("div.hp.column.one, div.hp.column.two").height(""); 	
		        $("div.hp.column.three").height("");
			var hpHeight = $("div#hp-content").height();
			$("div.hp.column.one, div.hp.column.two").height(hpHeight+30); 	
			$("div.hp.column.three").height(hpHeight+140); 

			var spHeight = 	$("div#sp-content").height();
			$("div.sp.column").height(spHeight);
		});
	}
	function dynamicContent() {
		//DOC .sw-mystart-button.home -- ELEMENT districtButton SHOWS OR HIDES THE DISTRICT HOME BUTTON
		var districtButton = 'true';
		if(districtButton == "false"){
			$(".sw-mystart-button.home").hide();
		}
		// #sp-breadcrumbs -- ELEMENT breadcrumbs SHOWS OR HIDES THE BREADCRUMBS
		var breadcrumbs = 'true';
		if(breadcrumbs == "false"){
			$("#sp-breadcrumbs").hide();
		}
		// #gb-logo -- ELEMENT showSchoolLogo SHOWS OR HIDES THE SCHOOL LOGO
		var showLogo = 'false'; 
		if(showLogo == "true"){
			$("#gb-logo").html('');
		}

		$("ul.sw-channel-list li.sw-channel-item:last").css("border", "0px");
		$("#swsignin-txt-username").focus();
	}
