var audioElementyoutuberoar;			
audioElementyoutuberoar = document.createElement('audio');
audioElementyoutuberoar.setAttribute('src', 'assets/lion-roar.mp3');
audioElementyoutuberoar.volume = 0;
$.get();		
audioElementyoutuberoar.addEventListener('load', function() {}, true);	
var myAudioyoutuberoar = $(audioElementyoutuberoar);

if ('hover' == 'hover') {
	$('.youtuberoar').hover(function (){									
		playObjyoutuberoar();			
	}, function () {
		stopObjyoutuberoar();
	});	
} else {
	$('.youtuberoar').click(function (){
		if(audioElementyoutuberoar.paused || audioElementyoutuberoar.ended) {
			playObjyoutuberoar();			
		} else {
			stopObjyoutuberoar();
		}
	});
}

function stopObjyoutuberoar(){					
	myAudioyoutuberoar.stop().animate({volume: 0}, 0000, 
	function() { 
		audioElementyoutuberoar.pause(); 
		audioElementyoutuberoar.currentTime = 0; 
	});								
}

function playObjyoutuberoar(){					
	audioElementyoutuberoar.volume = 0;
	audioElementyoutuberoar.play();
	myAudioyoutuberoar.stop().animate({volume: 1}, 0000);								
}


