div.script:
var onProcess = false;
jQuery(document).ready(function($){
	var acMenu = $("#accordion_menu_114");
	acMenu.find("a").click(function(){
		if ($(this).attr("target") == '_blank') {
			window.open($(this).attr("href"));
		} else {
			location = $(this).attr("href");
		}
		return false;
	});

	try{
		var current = $("#accordion_menu_114 li.opened");
		var root = current.parents('.accordion-menu'), lis = current.parents('li');
		$('li', root).filter(lis).addClass('opened');
	} catch(e){
		console.log(e.message);
	}
	$("#accordion_menu_114 li.opened > .ul-wrapper").css("display","block");
	$("#accordion_menu_114 li.opened > .item-wrapper .menu-button img").attr("src", "/modules/mod_cinch_menu/tmpl/images/minus.png");

	acMenu.find(".item-wrapper").click(function(){
		var li = $(this).parent('li');
		if(li.hasClass("opened")){
			// Close this item and once slideUp is complete, ensure children are also closed
			li.children(".ul-wrapper").slideUp(300, function() {
				li.find(".item-wrapper > .menu-button > img").attr("src", "/modules/mod_cinch_menu/tmpl/images/plus.png");
				li.find("li.opened").removeClass("opened");
				li.find(".ul-wrapper").css("display","none");
				li.removeClass("opened");
			});
		}else{
			// Close all siblings (and their children) and open this one
			var openedLi = li.parent().children("li.opened");
			openedLi.children(".ul-wrapper").slideUp(300, function() {
				openedLi.find(".item-wrapper > .menu-button > img").attr("src", "/modules/mod_cinch_menu/tmpl/images/plus.png");
				openedLi.find("li.opened").removeClass("opened");
				openedLi.find(".ul-wrapper").css("display","none");
				openedLi.removeClass("opened");
			});
			var ul = li.children(".ul-wrapper");
			if(ul.length != 0){
				li.addClass("opened");
				li.children(".item-wrapper").children(".menu-button").children("img").attr("src", "/modules/mod_cinch_menu/tmpl/images/minus.png");
				ul.slideDown(300);
			}
		}
		return false;
	});
});
