var ElementQueries = Class.$extend({
    __init__: function () {
        ElementQueries.pageInstance = this;
        this.setupElementQueries();
        jQuery(window).resize(jQuery.proxy(this.setupElementQueries,this));
    },

    setupElementQueries: function() {
        jQuery(".jsUsePoorMansElementQueries").each(jQuery.proxy(this.setupCSSModifierClasses,this));
    },

    setupCSSModifierClasses: function(index, element) {
        var width = jQuery(element).width();
        var currentClass = jQuery(element).data("current_eq_class");
        //sl.log("width:" + width + " current:" + currentClass);
        var changedState = false;
        if ( width <= 320 )
        {
            if ( (currentClass == null) || (currentClass != "sl_element_query_mobile_portrait") )
            {
                if ( currentClass != null ) {
                    jQuery(element).removeClass(currentClass);
                }
                jQuery(element).addClass("sl_element_query_mobile_portrait");
                jQuery(element).data("current_eq_class","sl_element_query_mobile_portrait");
                changedState = true;
            }
        }
        else if ( width <= 480 )
        {
            if ( (currentClass == null) || (currentClass != "sl_element_query_mobile_landscape") )
            {
                if ( currentClass != null ) {
                    jQuery(element).removeClass(currentClass);
                }
                jQuery(element).addClass("sl_element_query_mobile_landscape")
                jQuery(element).data("current_eq_class","sl_element_query_mobile_landscape");
                changedState = true;
            }
        }
        else if ( width <= 768 )
        {
            if ( (currentClass == null) || (currentClass != "sl_element_query_ipad_portrait") )
            {
                if ( currentClass != null ) {
                    jQuery(element).removeClass(currentClass);
                }
                jQuery(element).addClass("sl_element_query_ipad_portrait")
                jQuery(element).data("current_eq_class","sl_element_query_ipad_portrait");
                changedState = true;
            }
        }
        else if ( width <= 1024 )
        {
            if ( (currentClass == null) || (currentClass != "sl_element_query_ipad_landscape") )
            {
                if ( currentClass != null ) {
                    jQuery(element).removeClass(currentClass);
                }
                jQuery(element).addClass("sl_element_query_ipad_landscape")
                jQuery(element).data("current_eq_class","sl_element_query_ipad_landscape");
                changedState = true;
            }
        }
        else
        {
            if ( (currentClass == null) || (currentClass != "sl_element_query_desktop") )
            {
                if ( currentClass != null ) {
                    jQuery(element).removeClass(currentClass);
                }
                jQuery(element).addClass("sl_element_query_desktop")
                jQuery(element).data("current_eq_class","sl_element_query_desktop");
                changedState = true;
            }
        }
        if (changedState == true  ) {       // check currentClass so doesn't call on initial check

            jQuery(element).trigger("containerChangedSize")
        }
    },

    __classvars__: {
        RefreshElementQueries: function()
        {
            if ( ElementQueries.pageInstance != null )
            {
                ElementQueries.pageInstance.setupElementQueries();
            }
        }
    }
});


