var AccordionDropdowns = Class.$extend({
    __init__: function () {
        this.activeClass = "current";

        var d1 = new Dropdown();
        var d2 = new CMS2TitleAccordion();
        var d3 = new DropdownCheckbox();
        var d4 = new DropdownStayOpen();
        var d5 = new CMS2DropdownDynamic();
        var d6 = new DropdownInline();
        //var d6 = new CMS2DropdownMegaDynamic();

        this.buttonSelector = "set in subclass!";
        this.setupGlobalListener();
    },

    setupGlobalListener: function () {
        jQuery("body").on("click", ("." + AccordionDropdowns.buttonClass), jQuery.proxy(this.globalClickHandler, this));

        jQuery(document).on({
            keyup: jQuery.proxy(this.keyupDefaultHandler, this)
        }, ".jsDropdownMenu ." + AccordionDropdowns.buttonClass + ", .jsDropdownCheckbox ." + AccordionDropdowns.buttonClass);

        jQuery(document).on({        //to handle tabbing
            keydown: jQuery.proxy(this.keydownHandler, this)
        }, ".jsDropContent :tabbable");

        jQuery(document).on({
            keyup: jQuery.proxy(this.keyupInlineHandler, this)
        }, ".jsDropdownInline ." + AccordionDropdowns.buttonClass);  //to handle clicking clicking enter for 508
    },


    globalClickHandler: function (ev) {  //maybe unneccessary if all jsDropButtons are now divs
        ev.preventDefault();
        ev.stopPropagation();
    },

    clickHandler: function (ev) {
        //just handles the setting of elements
        this.button = jQuery(ev.currentTarget);
        this.container = this.button.closest("[class^='jsDropdown']");
        this.hiddenContent = this.container.find(".jsDropContent:first");
    },

    setFromTarget: function (target) {
        //just handles the setting of elements
        this.button = target
        this.container = this.button.closest("[class^='jsDropdown']");
        this.hiddenContent = this.container.find(".jsDropContent:first");
    },

    toggleDropdown: function (el) {
        var isVisible = (this.hiddenContent.is(":visible"));
        if (isVisible) {
            this.hiddenContent.slideUp("fast")
                .attr("aria-hidden", "true");
            this.button.removeClass(this.activeClass).blur();
        } else {
            this.hiddenContent.slideDown("fast", jQuery.proxy(this.slideDownCallback, this))
                .attr("aria-hidden", "false");
            this.button.addClass(this.activeClass);
        }
        return !isVisible;
    },

    slideDownCallback: function () {
        var scrollTo = this.button.data("scrollto");
        if (scrollTo != null) {
            sl.showHidden(jQuery(scrollTo));
        }
    },

    setupListeners: function () {
        jQuery("body").on("click", this.buttonSelector, jQuery.proxy(this.clickHandler, this));
    },

    closeOthers: function () {
        this.button.removeClass("current").blur();
        var current = jQuery(".jsDropdownMenu ." + AccordionDropdowns.buttonClass + ".current, .jsDropdownCheckbox ." + AccordionDropdowns.buttonClass + ".current");
        var i;
        for (i = 0; i < current.length; i++) {
            this.hideDropdown(current[i].closest("[class^='jsDropdown']"));
        }
    },

    keyupDefaultHandler: function (event) {
        var code = (event.keyCode ? event.keyCode : event.which);
        if (code == sl.keyboard["ENTER"]) {
            if (jQuery(jQuery(event.target)).hasClass(AccordionDropdowns.buttonClass)) {
                this.clickHandler(event);
                this.closeOthers();
                this.toggleDropdown();
                this.button.focus();
            }
        }
    },

    keydownHandler: function (event) {
        var code = (event.keyCode ? event.keyCode : event.which);
        sl.log("keydownHandler")
        if (code == sl.keyboard["TAB"]) {
            if (jQuery(event.target).is(jQuery(event.target).closest(".jsDropContent").find(":tabbable:last"))) {
                this.clickHandler(event);
                this.hideDropdown(this.container)
            }
        }
    },

    keyupInlineHandler: function (event) {
        var code = (event.keyCode ? event.keyCode : event.which);
        if (code == sl.keyboard["ENTER"]) {
            this.clickHandler(event);
            var ajaxContent = this.getAjaxContent();
            if (!ajaxContent) {
                var isVisible = this.toggleDropdown();
            } else {
                ajaxContent.success(jQuery.proxy(this.addAjaxContent, this));
            }
        }
    },

    getAjaxContent: function () {
        var url = this.button.data("url");
        var result = false;
        if (this.hiddenContent.is(':empty') && url != null) {
            var variables = {}
            sl.addAjaxScope(variables);
            var result = jQuery.ajax({
                data: variables,
                url: url
            });
            result.error(function (data) {
                sl.log(data.responseText);
            });
        }
        return result;
    },

    addAjaxContent: function (data) {
        this.hiddenContent.html(data);
        var isVisible = this.toggleDropdown();
        this.button.addClass(this.activeClass);
        this.button.attr("aria-expanded", "true");
    },

    hideDropdown: function (container) {
        jQuery(container).find(".jsDropContent:first").hide();
        jQuery(container).find(".jsDropButton:first").removeClass(this.activeClass);
    },

    showDropdown: function (container) {
        jQuery(container).find(".jsDropContent:first").show();
        jQuery(container).find(".jsDropButton:first").addClass(this.activeClass);
    },

    __classvars__: {
        buttonClass: "jsDropButton"
    }
});


