    __init__: function () {
        this.containerClass = "jsDropdownInline";
        this.buttonSelector = "." + this.containerClass + " ." + AccordionDropdowns.buttonClass;
        this.setupListeners();
    },

    clickHandler: function (ev) {
        this.$super(ev);
        var ajaxContent = this.getAjaxContent();
        if (!ajaxContent) {
            var isVisible = this.toggleDropdown();
        } else {
            ajaxContent.success(jQuery.proxy(this.addAjaxContent, this))
        }
    }

});


