var CMS2TitleAccordion = AccordionDropdowns.$extend({
    __init__: function () {
        this.containerClass = "jsCMS2Block";
        this.activeClass = "sl-cms2-title-alignment--accordion__open";
        this.buttonClass = "jsTitleAccordion";
        this.buttonSelector = "." + this.containerClass + " ." + this.buttonClass;
        this.hiddenContentClass = "jsTitleAccordionContent";
        this.setupListeners();
    },
    setupListeners: function () {
        this.$super();
        jQuery(document).on({
            keyup: jQuery.proxy(this.keyupAccordion, this)
        }, ".jsTitleAccordion");
        jQuery(document).on({
            'jsVideoLoaded': CMS2TitleAccordion.handleLoadedContent
        })
    }
    ,
    fixupAccordionsWithoutTitles: function () {
        var blocksWithAccordions = jQuery("." + this.hiddenContentClass).closest("." + this.containerClass);
        jQuery.each(blocksWithAccordions, jQuery.proxy(this.checkIfWithoutTitle, this))
    },

    checkIfWithoutTitle: function (key, value) {
        var block = jQuery(value);
        if (block.find("." + this.buttonClass).length == 0) { //there's no title/button element if it's empty
            block.find("." + this.hiddenContentClass).show()
        }
    },
    keyupAccordion: function (event) {
        sl.log("keyupAccordion", event);
        var code = (event.keyCode ? event.keyCode : event.which);
        if (code == sl.keyboard["ENTER"]) {
            this.clickHandler(event);
            this.closeOthers();
            this.button.focus();
        }
    },

    clickHandler: function (ev) {
        sl.log("CMS2TitleAccordion clickHandler")
        jQuery(".jsTitleAccordionContent").css({
            position: "block"
        })
        this.button = jQuery(ev.currentTarget);
        this.container = this.button.closest("." + this.containerClass);
        this.hiddenContent = this.container.find(".jsTitleAccordionContent");
        if (this.hiddenContent.length == 1) { // so no toggling if in edit mode
            var isVisible = this.toggleDropdown();
            if (isVisible == true) {
                this.button.attr("aria-expanded", "true");
            } else {
                this.button.attr("aria-expanded", "false");
            }
        }
    },
    __classvars__: {
        handleLoadedContent: function (event) {
            sl.log("handleLoadedContent")
            if (event.originalEvent){
                jQuery(event.originalEvent.detail.frameElement).closest(".jsTitleAccordionContent").hide();
            }
        }
    }
});

