var DropdownCheckbox = AccordionDropdowns.$extend({
    __init__: function () {
        this.containerClass = "jsDropdownCheckbox"
        this.buttonSelector = "." + this.containerClass + " ." + AccordionDropdowns.buttonClass;
        this.setupListeners();
    },

    clickHandler: function (ev) {
        this.$super(ev);
        sl.log("DropdownCheckbox clicked")
        var isVisible = this.toggleDropdown();

        if (isVisible) {
            jQuery(this.hiddenContent).on("click", function (ev) {
                ev.stopPropagation();
            });
            jQuery(document).on("click", jQuery.proxy(this.hideDropdown, this, this.container))
        }

    }

})


