var DropdownStayOpen = AccordionDropdowns.$extend({
    __init__: function () {
        this.containerClass = "jsDropdownStayOpen"
        this.buttonSelector = "." + this.containerClass + " ." + AccordionDropdowns.buttonClass;
        this.setupListenersElement();
    },

    clickHandler: function (event) {
        this.$super(event);
        sl.log("clickHandler button tareget", this.button);
        var button = jQuery(event.target);
        var container = button.closest("[class^='jsDropdown']").first();
        var hiddenContent = container.find(".jsDropContent").first();
        sl.log("DropdownStayOpen clicked")
        var isVisible = this.toggleDropdownElement(button, container, hiddenContent);
    },

    toggleDropdownElement: function (button, container, hiddenContent) {
        var isVisible = (hiddenContent.is(":visible"));
        if (isVisible) {
            hiddenContent.slideUp("fast").attr("aria-hidden", "true").removeClass("jsCurrent");
            button.removeClass("sl-cms2-dropdown-menu__button—current").blur();
        } else {
            if ((this.oldOpen != null) && (this.oldOpen.parents(".jsDropdownStayOpen").length > 1 && this.oldOpen !== hiddenContent)) {
                this.oldOpen.slideUp("fast").attr("aria-hidden", "true").removeClass("jsCurrent");
            }
            if ((this.oldOpen != null) && this.oldOpen.parents(".jsDropdownStayOpen").length >= 1 && button.parents(".jsDropdownStayOpen").length == 1) {
                this.oldOpen.parents(".jsDropdownStayOpen").find(".jsCurrent").slideUp("fast").attr("aria-hidden", "true").removeClass("jsCurrent");
            }
            hiddenContent.slideDown("fast").attr("aria-hidden", "false").addClass("jsCurrent");
            this.fixOffset(hiddenContent, button)
            button.addClass("sl-cms2-dropdown-menu__button—current");
            this.oldOpen = this.hiddenContent;
            hiddenContent.find(":tabbable:first").focus();

        }
        return !isVisible;
    },

    fixOffset: function(hiddenContent, button) {
      var offset = hiddenContent.offset();
      if (offset.left + hiddenContent.width() > jQuery(window).width()){
          hiddenContent.css("left", button.offset().left + button.width() - hiddenContent.width())
      }
    },

    setupListenersElement: function () {
        jQuery("body").on("click", ".jsDropdownStayOpen > ." + AccordionDropdowns.buttonClass, jQuery.proxy(this.clickHandler, this));
    }


})

