var CMS2DropdownMegaDynamic = AccordionDropdowns.$extend({
    __init__: function () {
        this.containerClass = "jsDropdownDynamicMegaContent";
        this.buttonSelector = "." + this.containerClass + " ." + AccordionDropdowns.buttonClass;
        this.setupListeners();
        jQuery(this.buttonSelector).closest(".jsNavigationPlayerElement").data("player-handler", this);
        sl.log("CMS2DropdownMegaDynamic init buttonSelector", this.buttonSelector)
    },

    clickHandler: function (ev) {
        this.$super(ev);
        this.closeOthers();
        this.loadOrToggle();
        this.button.focus();
    },
    loadOrToggle: function () {
        if (this.hiddenContent.is(':empty')) {
            var getURL = "/pf4/cms2/view_page";
            var variables = {};
            var pageParameterName = this.button.data("page-parameter-name");
            var pageParameterValue = this.button.data("page-parameter-value");
            var mod = this.button.data("content-id");
            variables[pageParameterName] = pageParameterValue;
            variables["mod"] = mod;
            sl.addAjaxScope(variables);
            var setupAjax = jQuery.ajax({
                url: getURL,
                type: "GET",
                data: variables,
                context: this,
                success: jQuery.proxy(this.loadHiddenContent, this)
            });
        }
        else {
            this.doToggle()
        }
    },
    loadHiddenContent: function (data) {
        this.hiddenContent.html(data);
        this.doToggle()
    },
    doToggle: function () {
        var buttonOffsetLeft = this.button.offset().left
        var body = this.hiddenContent.closest("body")
        var bodyWidth = body.innerWidth();
        var maxWidth = parseInt(this.hiddenContent.css("max-width"), 10);
        if (bodyWidth > maxWidth) {
            var leftMargin = (bodyWidth - maxWidth) / 2;
            var leftDifferential = leftMargin - buttonOffsetLeft;// + body.offset().left;
        } else {
            bodyWidth = bodyWidth * .8;
            var leftMargin = bodyWidth * .1;
            var leftDifferential = leftMargin - buttonOffsetLeft;// - leftMargin);// + body.offset().left;
        }
        this.hiddenContent
            .css({
                "width": bodyWidth,
                "left": leftDifferential
            });
        var isVisible = this.toggleDropdown();
        if (isVisible) {
            jQuery(document).on("click", jQuery.proxy(this.hideDropdown, this, this.container))
        }
        this.button.focus();
    },

    close: function (target) {
        this.setFromTarget(target);
        this.hideDropdown(this.container)
    },
    open: function (target) {
        this.setFromTarget(target);
        this.loadOrToggle();
    },
    isOpen: function (target) {
        this.setFromTarget(target);
        return (this.hiddenContent.is(":visible"));
    }
});


