var Dialog = Class.$extend({
    __init__: function (options) {
        sl.log("Dialog Class");
        this.block_id = options.block_id;
        this.dialogCallback = options.dialogCallback;
        this.url = options.url || null;
        this.additonalData = options.additonalData || null;
        this.contentClassName = options.contentClassName || null;
        this.html = options.html || null;
        this.showCloseButton = options.showCloseButton || false;
        this.buttons = options.buttons || null;
        this.submitFunction = options.submitFunction;
    },

    createDialog: function () {
        this.currentDialogeferred = jQuery.Deferred();
        jQuery.when(this.fetchDialog(this.url)).then(jQuery.proxy(this.openDialog, this));
        return this.currentDialogeferred;
    },

    openDialog: function (data) {
        var options = {
            message: "",
            input: data,
            contentClassName: this.contentClassName,//this.contentClassName,
            callback: this.dialogCallback,
            showCloseButton: this.showCloseButton
        };
        if (this.buttons != null) {
            options.buttons = this.buttons;
        }
        this.vexInstance = vex.dialog.open(options);
        this.currentDialogeferred.resolveWith(this, this.vexInstance);
    },

    fetchDialog: function (url) {
        var fetchAjax = jQuery.ajax({
            url: url,
            data: this.additonalData
        });
        return fetchAjax;
    },

    createDialogWithHtml: function (openCallback) {
        var options = {
            afterOpen: openCallback,
            message: "",
            input: this.html,
            contentClassName: this.contentClassName,//this.contentClassName,
            callback: this.dialogCallback,
        }

        if (this.submitFunction != null ){
            options.onSubmit = this.submitFunction;
        }
        if (this.buttons != null) {
            options.buttons = this.buttons;
        }
        return vex.dialog.open(options);
    },

    close: function() {
        vex.close(this.vexInstance.data().vex.id);
    }
});


var SLFormDialog = Class.$extend({
    __init__: function (options) {
        this.url = options.url;
        this.variables = options.variables;
        this.formArea = options.formArea;
        this.submitCallback = options.submitCallback;
        this.cancelCallback = options.cancelCallback;
        this.dialogCssClasses = options.dialogCssClasses;
    },

    open: function() {
        this.loadForm();
    },

    loadForm: function () {
        sl.addAjaxScope(this.variables);
        jQuery.ajax({
            url: this.url,
            type: "GET",
            data: this.variables,
            context: this,
            success: jQuery.proxy(this.loadFormSuccess,this)
        });
    },

    loadFormSuccess: function (data) {
        var formID = jQuery(data).find("form").attr("id");
        this.findButtons(data);
        this.setFormID(formID);
        var dialogArea = this.getFormArea();
        dialogArea.empty();
        dialogArea.html(data);
        this.activateButtons();
        this.createDialog(dialogArea);
    },

    createDialog: function (dialogArea) {
        this.dialogArea = dialogArea;
        var classes = {
            "ui-dialog": this.dialogCssClasses
        }
        return this.dialogArea.dialog({
            resizable: false,
            modal: true,
            hide: "fadeOut",
            classes: classes
        });
        //this.dialogArea.dialog();
    },

    getFormArea: function () {
        var result = jQuery("#" + this.formArea);
        return result;
    },

    preventDefaultSubmitBehavior: function () {
        jQuery("#" + this.getFormID()).on("submit", function (event) {
            event.preventDefault();  // prevent default submit
        });
    },

    setFormID: function (value) {
        this.formID = value;
    },

    getFormID: function (value) {
        return this.formID;
    },

    findButtons: function (html) {
        this.buttonIDs = [];
        jQuery(html).find("button").each(jQuery.proxy(this.recordButton, this));
    },

    recordButton: function (index, element) {
        var buttonID = jQuery(element).attr("id");
        this.buttonIDs.push(buttonID);
    },

    activateButtons: function () {
        this.preventDefaultSubmitBehavior();
        if (this.buttonIDs != null) {
            for (var i = 0; i < this.buttonIDs.length; i++) {
                var buttonID = this.buttonIDs[i];
                var event = jQuery("#" + buttonID).attr("event");
                var button = jQuery("#" + buttonID);
                button.attr("onclick", null);
                if (event == "cancel") {
                    button.click(jQuery.proxy(this.cancelDialogHandler, this));
                }
                else {
                    button.click(jQuery.proxy(this.submitDialogHandler, this));
                }
            }
        }
    },


    close: function() {
        this.dialogArea.dialog("close");
        this.dialogArea = null;
    },

    cancelDialogHandler: function (event) {
        this.dialogArea.dialog("close");
        this.dialogArea = null;
        if ( this.cancelCallback != null )
        {
            this.cancelCallback();
        }
    },

    submitDialogHandler: function (event) {
        var form = sl.ajaxSubmit("submit", this.getFormID(), {
            context: this,
            dataType: "json",
            success: function (response) {
                if ((response != null) && (response.statusCode == "reload")) {
                    // probably missing required field
                    var variables = {};
                    sl.addAjaxScope(variables);
                    jQuery.ajax({
                        url: response.url,
                        type: "GET",
                        data: variables,
                        context: this,
                        success: function (data) {
                            var formID = jQuery(data).find("form").attr("id");
                            this.findButtons(data);
                            this.setFormID(formID);
                            this.dialogArea.html(data);
                            this.activateButtons();
                        }
                    });
                }
                else {
                    this.dialogArea.dialog("close");
                    this.dialogArea = null;
                    if ( this.submitCallback != null )
                    {
                        this.submitCallback(response);
                    }
                }
            }
        });
    }
});

