var LongActionMonitor = Class.$extend({
    __init__: function(holder, url, wait, interval, button)
    {
        this.url = url;
        this.wait = wait;
        this.interval = interval;
        this.stopped = false;
        this.button = button;
        this.openedDialog = false;
        this.holder = holder;
        setTimeout( jQuery.proxy(this.firstInterval,this), wait);
    },

    firstInterval: function()
    {
        sl.log("first interval");
        if ( this.stopped == false )
        {
            sl.log("set cursor");
            jQuery("body").css("cursor", "progress");
            if ( this.button != null )
            {
                this.button.css("cursor", "progress");
            }
            setTimeout( jQuery.proxy(this.laterInterval,this), this.interval);
        }
    },

    laterInterval : function()
    {
        if ( this.stopped == false )
        {
            sl.log("update long action");
            if ( this.url != null )
            {
                sl.log("fetching status");
                var variables = {};
                var url = this.url;
                sl.addAjaxScope(variables);
                jQuery.ajax({
                    url: url,
                    type: "GET",
                    data: variables,
                    dataType: "json",
                    context: this,
                    success: function (data)
                    {
                        if ( this.stopped == false )
                        {
                            if ( (this.openedDialog == false) )
                            {
                                sl.longActionStatusOpen();
                                this.openedDialog = true;
                            }
                            if ( data.title != null )
                            {
                                jQuery("#jsLongActionTitle").text(data.title);
                            }
                            if ( data.status != null )
                            {
                                jQuery("#jsLongActionStatus").text(data.status);
                                if ( data.percentComplete != null )
                                {
                                    jQuery("#jsLongActionProgressBar").progressbar( {max: 100, value: data.percentComplete} );
                                }
                            }
                            else
                            {
                                jQuery("#jsLongActionStatus").empty();
                                jQuery("#jsLongActionProgressBar").progressbar( {value: false} );
                            }
                            setTimeout( jQuery.proxy(this.laterInterval,this), this.interval);
                        }
                    }
                });
            }

        }
    },

    stop: function()
    {
        sl.log("stop");
        this.holder.longActionMonitor = null;
        this.stopped = true;
        jQuery("body").css("cursor", "default");
        if ( this.button != null )
        {
            this.button.css("cursor", "default");
        }
        if ( this.openedDialog )
        {
            sl.longActionStatusClose();
            this.openedDialog = false;
        }
    }


});


