var DropzoneEffect = Class.$extend({
    __init__: function (options) {
        var defaults = {
            dragOverlayClass: "dropzone-effect",
            dropOverlayClass: "jsOverDropzone",
            dragHereMessageClass: "jsDnDMessage",
            findTop: null,
            messageClass : "jsEmbedFile"
        };
        var settings = jQuery.extend({}, defaults, options);
        if (!DropzoneEffect.isSupported()) {
            jQuery("." + settings.dragHereMessageClass).hide();
            return;
        }
        this.dropzone = settings.dropzoneElement;
        this.messageClass = settings.messageClass;
        this.findDropZone = settings.findDropZone || null;
        this.findTop = settings.findTop;

        this.dragOverlayClass = settings.dragOverlayClass;
        this.dropOverlayClass = settings.dropOverlayClass;
        this.dndMessageClass = settings.dndMessageClass;
        this.dropzone.css("position", "relative");
        this.jsDropzoneEffect = null;
        this.dropBodyTimeout = 0;
        this.initListeners();
    },
    initListeners: function () {
        jQuery("body").on({
            'dragover': jQuery.proxy(this.bodyEnterHandler, this)
        });
        this.dropzone.on({
            'dragenter': jQuery.proxy(this.zoneEnterHandler, this),
            'dragleave': jQuery.proxy(this.zoneLeaveHandler, this)
        }, "." + this.dragOverlayClass);
    },


    bodyEnterHandler: function () {
        var element = this.dropzone;
        if (this.findDropZone != null) {
            element = this.findDropZone(this.dropzone);
        }
        if (this.dropBodyTimeout == 0) {
            if (element.find("." + this.dragOverlayClass).length == 0) {
                var top = (this.findTop != null) ? this.findTop() : 0;
                this.jsDropzoneEffect = jQuery('
')
                    .addClass(this.dragOverlayClass)
                    .addClass(this.messageClass)
                    .css({
                        position: "absolute",
                        width: element.width(),
                        height: element.height(),
                        top: top
                    })
                    .appendTo(element);

            }
        } else {
            clearTimeout(this.dropBodyTimeout);
        }
        this.dropBodyTimeout = setTimeout(
            jQuery.proxy(this.dropBodyTimeoutEnd, this), 100);
    },

    dropBodyTimeoutEnd: function () {
        this.dropBodyTimeout = 0;
        jQuery("." + this.dragOverlayClass).remove();
    },

    clearDragStyle: function () {
        if(this.jsDropzoneEffect != null){
            this.jsDropzoneEffect.removeClass(this.dropOverlayClass);
        }
    },

    zoneLeaveHandler: function () {
        this.clearDragStyle();
    },

    zoneEnterHandler: function (e) {
        var timeout = window.dropZoneTimeout;
        if (!timeout) {
            this.jsDropzoneEffect.addClass(this.dropOverlayClass);
        } else {
            clearTimeout(timeout);
        }
        window.dropZoneTimeout = setTimeout(function () {
            window.dropZoneTimeout = null;
        }, 100);
    },
    __classvars__: {
        isSupported: function () {
            return jQuery.support.xhrFormDataFileUpload;
        }
    }

})


