var SelectableModule = Class.$extend({
    __init__ : function(moduleID, selectableClass, refreshURL, editURL, refreshArea, groupID)
    {
        this.moduleID = moduleID;
        this.selectableClass = selectableClass;
        this.selectedID = null;
        this.refreshURL = refreshURL;
        this.editURL = editURL;
        this.refreshArea = refreshArea;
        this.groupID = groupID;
        this.useEdit = false;
        jQuery("#" + moduleID).data("selectableModule", this);
        jQuery(document).on("click", "." + this.selectableClass, SelectableModule.selectHandler);
        sl.log("selectable created");
    },

    setRefreshURL: function(url)
    {
        this.refreshURL = url;
    },

    setEditURL: function(url)
    {
        this.editURL = url;
    },

    setRefreshArea: function(id)
    {
        this.refreshArea = id;
    },

    setUseEdit: function(value)
    {
        this.useEdit = value;
    },

    getCurrentSelection : function()
    {
        return this.selectedID;
    },

    clearCurrentSelection: function()
    {
        jQuery("#" + this.moduleID).find(".SelectableHilite").removeClass("SelectableHilite");
        this.selectedID = null;
    },

    select: function(selected, ev)
    {
        if ( selected != null )
        {
            jQuery("#" + this.moduleID).find(".SelectableHilite").removeClass("SelectableHilite");
            this.selectedID = jQuery(selected).attr("jsID");
            jQuery(selected).addClass("SelectableHilite");
        }
        else
        {
            this.selectedID = null;
        }
    },

    hiliteSelection: function()
    {
        var selectedID = this.selectedID;
        jQuery("." + this.selectableClass).each(function(index)
        {
            var id = jQuery(this).attr("jsID");
            if ((id != null) && (id == selectedID))
            {
                jQuery(this).addClass("SelectableHilite");
                return false;
            }
        });
    },

    selectByID: function(id)
    {
        var foundElement = null;
        jQuery("." + this.selectableClass).each(function(index, element)
        {
            var elementID = jQuery(element).attr("jsID");
            if (id == elementID)
            {
                foundElement = element;
                return false;
            }
            else
            {
                return true;
            }
        });
        this.select(foundElement, null);
    },

    __classvars__ : {
        findModule: function(start)
        {
            var result = sl.findDataInParent(start, "selectableModule");
            return result;
        },

        selectHandler: function(ev)
        {
            var moduleData = SelectableModule.findModule(this);
            moduleData.select(this, ev)
        }
    }
});


