var Popup = Class.$extend({
    __init__: function (options) {
        sl.log("init Popup")
        this.moduleID = options.id;
        this.jsModule = jQuery("#" + this.moduleID)
        this.listenerClass = options.listenerClass;
        this.height = options.height;
        this.width = options.width;
        this.setupListeners();
    },
    setupListeners: function () {
        this.jsModule.on({"click": jQuery.proxy(this.clickHandler, this)}, "." + this.listenerClass)
    },

    clickHandler: function (event) {
        event.preventDefault()
        var thisButton = jQuery(event.target);
        var url = thisButton.data("url");
        sl.log("thisButton", thisButton, url);
        var period_id = thisButton.data("period_id")
        var variables = [];
        variables["group_id"] = thisButton.data("group_id");
        variables["period_id"] = period_id;
        url += "?group_id=" + variables["group_id"] + "&period_id=" + variables["period_id"] + "&ajax_scope=" + variables["ajax_scope"];//sending data didn't work
        jQuery("#jsStudentHeaderTemplate").clone().attr("id", "liveTemplate").appendTo("body");
        jQuery("#liveTemplate .jsPeriodNumber").text(thisButton.data("period_number"));
        jQuery("#liveTemplate .jsCalendarHolder").load(url);//load in the calendar on page for proper rendering
        jQuery("#liveTemplate .jsCalendarHolder").on({"calendarLoaded": jQuery.proxy(this.fillOpenBox, this)});
        jQuery("#liveTemplate").on({
            "click": jQuery.proxy(this.itemClickHandler, this)
        }, ".jsCalItem, .jsCalItemInner");
    },

    fillOpenBox: function () {
        jQuery.colorbox({rel: 'jsPopup',
            html: jQuery("#liveTemplate"),
            innerWidth: this.width,
            innerHeight: this.height,
            opacity: .6
        })
    },

    itemClickHandler: function (event) {
        event.stopPropagation()
    }

})


