var DateTime = Class.$extend({
    __init__: function (moduleID, start, end)
    {
        this.startId = "#" + start;
        this.endId = "#" +end;
        jQuery(this.startId).datepicker("option", {
            'beforeShow': jQuery.proxy(this.beforeShow, this),
            'onSelect': jQuery.proxy(this.onSelect, this)
        })

    },

    beforeShow: function()
    {
        this.startDate = jQuery(this.startId).datepicker("getDate");
        this.endDate = jQuery(this.endId).datepicker("getDate");
        this.dateDelta = (this.endDate - this.startDate) / (24 * 60 * 60 * 1000);
    },

    onSelect: function(dateText,instance)
    {
        var selectedStartDate = jQuery("#"+instance["id"]).datepicker("getDate");
        var dayOfWeek = selectedStartDate.getUTCDay();
        var d = jQuery.datepicker.parseDate('mm/dd/yy', dateText);//dateText
        var possibleEndDay = dayOfWeek + (this.dateDelta % 7);
        if (possibleEndDay == 6)
        {
            this.dateDelta += 2;
        }
        else if (possibleEndDay == 7)
        {
            this.dateDelta += 1;
        }
        if (selectedStartDate > this.endDate)
        {
            d.setDate(d.getDate() + this.dateDelta);
            jQuery(this.endId).datepicker('setDate', d);
        }
        else
        {
            d.setDate(d.getDate() + this.dateDelta);
        }
        jQuery(this.endId).datepicker("option", "minDate", dateText);
    }
})
