var DocumentAttachment = Class.$extend({
    __init__: function (formDataID, fieldName, fieldID, groupID, blockID, refreshURL, newLinkURL, uploadFileURL,
                        fromLockerURL, addLinkFromLockerURL, deleteLinkURL, toggleLinkShareModeURL, moveLinkURL)
    {
        this.formDataID = formDataID;
        this.fieldName = fieldName;
        this.fieldID = fieldID;
        this.groupID = groupID;
        this.blockID = blockID;
        this.refreshURL = refreshURL;
        this.newLinkURL = newLinkURL;
        this.uploadFileURL = uploadFileURL;
        this.fromLockerURL = fromLockerURL;
        this.addLinkFromLockerURL = addLinkFromLockerURL;
        this.deleteLinkURL = deleteLinkURL;
        this.toggleLinkShareModeURL = toggleLinkShareModeURL;
        this.moveLinkURL = moveLinkURL
        this.formID = null;
        this.fullFieldDiv = jQuery("#" + this.fieldID + "_full_field");
        this.fullFieldDiv.data("documentAttachment", this);
        this.dialogArea = null;
        this.buttonIDs = [];
        this.attachmentListArea = this.fullFieldDiv.find(".jsAttachmentProgressArea");
        this.jsItemTemplate = jQuery("#jsItemTemplate");
        this.fullFieldDiv.on("click", ".jsAttachmentNewLink", DocumentAttachment.newLinkHandler);
        this.fullFieldDiv.on("click", ".jsAttachmentUploadFile", DocumentAttachment.uploadFileHandler);
        this.fullFieldDiv.on("click", ".jsAttachmentFromLocker", jQuery.proxy(this.loadFromLocker, this));
        this.fullFieldDiv.on("click", ".jsDeleteHttpLink", DocumentAttachment.deleteLinkHandler);
        this.fullFieldDiv.on("click", ".jsToggleLinkShareMode", DocumentAttachment.toggleLinkShareModeHandler);
        this.fullFieldDiv.on("click", ".jsEmbedLink", jQuery.proxy(this.embedLink, this));

        this.initFileDrop();
        this.initDraggable();
        this.showEmbedControls();
    },

    showEmbedControls: function()
    {
        this.fullFieldDiv.trigger("slEventShowEmbeddedControls");
    },

    embedLink: function(ev)
    {
        var id = jQuery(ev.target).attr("jsID");
        var properties =  {};
        properties["http_link_form_data"] = this.formDataID;
        properties["field_name"] = this.fieldName;
        properties["id"] = id;
        properties["block_id"] = this.blockID;
        properties["attachments"] = this;
        jQuery(ev.target).trigger("slEventEmbedDocument",properties);
    },

    initFileDrop: function()
    {
        this.initProgressBar();
        var form = this.fullFieldDiv.closest("form");
        var textArea = form.find('textarea');
        var richTextEditor = RichTextEditor.findRichTextEditor(textArea);
        if ( richTextEditor !=  null )
        {
            richTextEditor.setFileDropFallback(this);
        }

        this.fullFieldDiv.fileupload({
            url:"/pf4/block_document/dropAttachment?http_link_form_data=" + this.formDataID + "&field_name=" + this.fieldName + "&group_id=" + this.groupID,
            sequentialUploads: true,
            maxFileSize: 70000000,
            done: jQuery.proxy(this.fileDropDoneHandler, this),
            add: jQuery.proxy(this.progressInstance.addHandler, this.progressInstance),
            progress: jQuery.proxy(this.progressInstance.progressHandler, this.progressInstance),
            dropZone:"#" + this.fieldID + "_full_field"
        });
        if (!DropzoneEffect.isSupported())
        {
            this.fullFieldDiv.find(".jsDragHelp").remove();
        }
    },

    fileAddHandler: function(uploadFile){
        this.fullFieldDiv.fileupload('add', {files: uploadFile });
    },

    initProgressBar: function()
    {
        var options = {
            itemTemplate : this.jsItemTemplate,
            itemsContainer: this.attachmentListArea
        }
        this.progressInstance = new ProgressBar(options);
    },

    initDraggable: function(ev)
    {
        this.getLinksArea().sortable({
            placeholder: 'ui-placeholder',
            update: jQuery.proxy(this.sortableUpdate, this),
            handle:".attachment-handle"
        });
    },

    sortableUpdate: function(event, ui)
    {
        var draggedID = jQuery(ui.item).attr("jsID");
        var destination = jQuery(ui.item).next().attr("jsID");
        if (destination == null)
        {
            destination = "end";
        }
        this.drop(draggedID, destination)
    },

    createDialog: function (dialogArea, height)
    {

        dialogArea.data("documentAttachment", this);
        this.dialogArea = dialogArea;
        this.dialogArea.dialog({
            resizable: false,
            modal: true,
            width: 800,
            height: height,
            hide: "fadeOut"
        })

        this.dialogArea.on({
            'keydown': function(event)
            {
                var enterTarget = jQuery(event.target);
                if (event.keyCode == 13)
                {  //handle enter key in forms
                    if (enterTarget.attr("id") == "link_title" && jQuery("#link_title").val() != null)
                    {
                        jQuery(event.target).closest("form").find("button:first").click();
                    }
                    if (enterTarget.attr("id") == "url" && jQuery("#url").val() != null)
                    {
                        jQuery(event.target).closest("form").find("button:first").click();
                    }
                    event.preventDefault();
                    return false;
                }
            }
        }, "input");

    },

    loadNewLink: function ()
    {
        var variables = {};
        variables["http_link_form_data"] = this.formDataID;
        variables["field_name"] = this.fieldName;
        sl.addAjaxScope(variables);

        jQuery.ajax({
            url: this.newLinkURL,
            type: "GET",
            data: variables,
            context: this,
            success: function (data)
            {
                var formID = jQuery(data).find("form").attr("id");
                this.findButtons(data);
                this.setFormID(formID);
                var dialogArea = this.getFormArea();
                dialogArea.empty().append(data);
                this.activateButtons();
                this.createDialog(dialogArea, 250);
            }
        });
    },

    loadUploadFile: function ()
    {
        sl.log("loadUploadFile")
        var variables = {};
        variables["http_link_form_data"] = this.formDataID;
        variables["field_name"] = this.fieldName;
        variables["group_id"] = this.groupID;
        sl.addAjaxScope(variables);
        jQuery.ajax({
            url: this.uploadFileURL,
            type: "GET",
            data: variables,
            context: this,
            success: function (data)
            {
                var formID = jQuery(data).find("form").attr("id");
                this.findButtons(data);
                this.setFormID(formID);
                this.getFormArea().empty().append(data);
                this.activateButtons();
                this.createDialog(this.getFormArea(), 250);
            }
        });
    },

    loadFromLocker: function ()
    {
        window[this.fieldID] = this; //
        var variables = {};
        variables["object_name"] = this.fieldID;
        sl.addAjaxScope(variables);
        jQuery.ajax({
            url: this.fromLockerURL,
            type: "GET",
            data: variables,
            context: this,
            success: function (data)
            {
                var formID = jQuery(data).find("form").attr("id");
                this.setFormID(formID);
                this.getFormArea().empty().append(data);
                this.createDialog(this.getFormArea(), 400);
            }
        });
    },

    selectResource: function(resourceID, title, url, embedURL, isGoogle, externalID)
    {
        sl.log("embedURL = " + embedURL);
        this.dialogArea.dialog("close");
        this.dialogArea = null;
        this.getFormArea().empty();
        var variables = {};
        sl.addAjaxScope(variables);
        variables["http_link_form_data"] = this.formDataID;
        variables["field_name"] = this.fieldName;
        variables["resource_id"] = resourceID;
        variables["title"] = title;
        variables["link_url"] = url;
        variables["embed_url"] = embedURL;
        if ( isGoogle )
        {
            variables["google_id"] = "true";
            variables["external_id"] = externalID;
        }
        jQuery.ajax({
            url: this.addLinkFromLockerURL,
            type: "GET",
            data: variables,
            context: this,
            success: function (data)
            {
                this.updateLinksArea();
            },
            error: function (data)
            {
                sl.alert(data.responseText);
            }
        });
    },

    findButtons: function(html)
    {
        jQuery(html).find("button").each(jQuery.proxy(this.recordButton, this));
    },

    recordButton: function(index, element)
    {
        var buttonID = jQuery(element).attr("id");
        this.buttonIDs.push(buttonID);
    },

    activateButtons: function ()
    {
        if ( this.buttonIDs != null )
        {
            for (var i = 0; i < this.buttonIDs.length; i++)
            {
                var buttonID = this.buttonIDs[i];
                var event = jQuery("#" + buttonID).attr("event");
                if (event == "cancel")
                {
                    jQuery("#" + buttonID).click(DocumentAttachment.cancelHandler);
                }
                else
                {
                    jQuery("#" + buttonID).click(DocumentAttachment.submitHandler);
                }
            }
        }
    },

    getFormArea: function ()
    {
        return jQuery("#" + this.fieldID + "_form_area");
    },

    getLinksArea: function ()
    {
        return jQuery("#" + this.fieldID + "_links_area");
    },

    setFormID: function (id)
    {
        this.formID = id;
    },

    submitCurrentForm: function ()
    {
        var form = sl.ajaxSubmit("submit", this.formID, {
            context: this,
            dataType: "json",
            success: function (response)
            {
                if ((response != null) && (response.statusCode == "reload"))
                {
                    // probably missing required field
                    var variables = {};
                    sl.addAjaxScope(variables);
                    jQuery.ajax({
                        url: response.url,
                        type: "GET",
                        data: variables,
                        context: this,
                        success: function (data)
                        {
                            var formID = jQuery(data).find("form").attr("id");
                            this.setFormID(formID);
                            this.getFormArea().empty().append(data);
                            this.activateButtons(formID);
                        }
                    });
                }
                else
                {
                    this.dialogArea.dialog("close");
                    this.dialogArea = null;
                    this.getFormArea().empty();
                    this.updateLinksArea();
                }
            }
        });
    },

    deleteLink: function (id)
    {
        var variables = {};
        sl.addAjaxScope(variables);
        variables["http_link_form_data"] = this.formDataID;
        variables["field_name"] = this.fieldName;
        variables["id"] = id;
        jQuery.ajax({
            url: this.deleteLinkURL,
            type: "GET",
            data: variables,
            context: this,
            success: function (data)
            {
                this.updateLinksArea();
            }
        });
    },

    toggleLinkShareMode: function (id)
     {
         var variables = {};
         sl.addAjaxScope(variables);
         variables["http_link_form_data"] = this.formDataID;
         variables["field_name"] = this.fieldName;
         variables["id"] = id;
         variables["group_id"] = this.groupID;
         jQuery.ajax({
             url: this.toggleLinkShareModeURL,
             type: "GET",
             data: variables,
             context: this,
             success: function (data)
             {
                 this.updateLinksArea();
             }
         });
     },

    updateLinksArea: function ()
    {
        var variables = {};
        sl.addAjaxScope(variables);
        variables["http_link_form_data"] = this.formDataID;
        variables["field_name"] = this.fieldName;
        jQuery.ajax({
            url: this.refreshURL,
            type: "GET",
            data: variables,
            context: this,
            success: function (data)
            {
                var linksArea = this.getLinksArea()
                linksArea.html(data);
                this.initDraggable();
                this.showEmbedControls();
            }
        });
    },

    cancelCurrentForm: function ()
    {
        this.getFormArea().empty();
        this.dialogArea.dialog("close");
        this.dialogArea = null;
    },

    drop: function(draggedID, destinationID)
    {
        var variables = {};
        sl.addAjaxScope(variables);
        variables["http_link_form_data"] = this.formDataID;
        variables["field_name"] = this.fieldName;
        variables["id"] = draggedID;
        variables["before_id"] = destinationID;
        jQuery.ajax({
            url: this.moveLinkURL,
            type: "GET",
            data: variables,
            context: this,
            success: function (data)
            {
                this.updateLinksArea();
            }
        });
    },

    fileDropDoneHandler: function (ev, data)
    {
        this.progressInstance.doneHandler(ev, data);
        ev.preventDefault();
        var response = data._response.result;
        if ((response != null) && (response == "err"))
        {
            sl.alert("There was a problem uploading the file.  The file may have a virus");
        }
        else
        {
            this.updateLinksArea();
        }
    },

    __classvars__: {
        findModule: function (start)
        {
            var result = sl.findDataInParent(start, "documentAttachment");
            return result;
        },

        newLinkHandler: function (event)
        {
            var module = DocumentAttachment.findModule(event.target);
            module.loadNewLink();
        },

        uploadFileHandler: function (event)
        {
            var module = DocumentAttachment.findModule(event.target);
            module.loadUploadFile();
        },

        deleteLinkHandler: function (event)
        {
            var id = jQuery(this).attr("jsID");
            var module = DocumentAttachment.findModule(event.target);
            module.deleteLink(id);
        },

        toggleLinkShareModeHandler: function (event)
        {
            var id = jQuery(this).attr("jsID");
            var module = DocumentAttachment.findModule(event.target);
            module.toggleLinkShareMode(id);
        },

        submitHandler: function (event)
        {
            event.preventDefault();
            var module = DocumentAttachment.findModule(event.target);
            module.submitCurrentForm();
        },

        cancelHandler: function (event)
        {
            event.preventDefault();
            var module = DocumentAttachment.findModule(event.target);
            module.cancelCurrentForm();
        }
    }
});
