var ImageFieldSelector = Class.$extend({
    __init__: function (fieldID, clickableID, popupURL, emptyImageURL)
    {
        this.fieldID = fieldID;
        this.clickableID = clickableID;
        this.popupURL = popupURL + "&field_name=" + fieldID;
        this.emptyImageURL = emptyImageURL;
        this.dialogPopupDiv = "#" + fieldID + "Dialog";
        this.dialogPopupContent = "#" + fieldID + "DialogContent"
        jQuery("#" + fieldID).data("imageField",this);
        jQuery("#" + clickableID).on("click", jQuery.proxy(this.openPopup, this));
        var clearID = "#" + fieldID+"_image_clear";
        sl.log("clearID:" + clearID);
        jQuery(clearID).on("click", jQuery.proxy(this.clearValue, this));

    },

    openPopup: function(event)
    {
        var variables = {};
        variables["id"] = this.documentID;
        sl.addAjaxScope(variables);
        jQuery.ajax({
            url: this.popupURL,
            type: "GET",
            context: this,
            data: variables,
            success: function(data) {
                this.dialogPopup = vex.open({
                    contentClassName: 'image-popup',
                    afterOpen: function($vexContent) {
                        return $vexContent.append(data);
                    }
                });
            }
        });
    },

    selectImageForEditing: function(imageEditURL)
    {
        this.close();
        var variables = {};
        sl.addAjaxScope(variables);
        jQuery.ajax({
            url: imageEditURL,
            type: "GET",
            context: this,
            data: variables,
            success: function(data) {
                this.dialogPopup = vex.open({
                    contentClassName: 'image-popup',
                    afterOpen: jQuery.proxy(function($vexContent) {
                        var result = $vexContent.append(data);
                        ImageEditor.setImageHolder(this);
                        return result;
                    },this)
                });
            }
        });
    },

    selectImage: function(imageURL,width,height)
    {
        jQuery("#" + this.fieldID).val(imageURL);
        jQuery("#" + this.clickableID).attr("src",imageURL);
        jQuery("#" + this.clickableID).width(width);
        jQuery("#" + this.clickableID).height(height);
        var imageElement = jQuery("#" + this.clickableID);
        imageElement.bind("load", jQuery.proxy(function ()
                {
                    sl.log("loading");
                },this));
        var value = jQuery("#" + this.fieldID).val();
        var src = jQuery("#" + this.clickableID).attr("src");
        sl.log("value:" + value + " src:" + src);
        this.close();
    },

    clearValue : function(event,ui)
    {
        jQuery("#" + this.fieldID).val("");
        jQuery("#" + this.clickableID).attr("src",this.emptyImageURL);
    },

    cancelImage: function()
    {
        this.close();
    },

    close: function()
    {
        vex.close(this.dialogPopup.data().vex.id);
    },

    __classvars__: {
        findModule : function(start)
        {
            var result = sl.findDataInParent(start, "imageField");
            return result;
        }
    }
});
