var ResourceFieldSelector = Class.$extend({
    __init__: function (fieldID, popupLinkID, clearLinkID, popupURL, idField, urlField, titleDisplay, nothingSelectedDisplay)
    {
        this.fieldID = fieldID;
        this.popupLinkID = popupLinkID;
        this.clearLinkID = clearLinkID;
        this.popupURL = popupURL;
        this.idField = idField;
        this.urlField = urlField;
        this.titleDisplay = titleDisplay;
        this.nothingSelectedDisplay = nothingSelectedDisplay;
        this.dialogPopupDiv = "#" + fieldID + "Dialog";
        this.dialogPopupContent = "#" + fieldID + "DialogContent"
        jQuery("#" + fieldID).data("resourceField",this);
        jQuery("#" + popupLinkID).on("click", jQuery.proxy(this.openPopup, this));
        jQuery("#" + clearLinkID).on("click", jQuery.proxy(this.clearValue, this));
    },


    // open popup pass object name into url
    openPopup: function(event)
    {
        window[this.fieldID] = this; //
        var variables = {};
        variables["object_name"] = this.fieldID;
        sl.addAjaxScope(variables);
        jQuery.ajax({
            url: this.popupURL,
            type: "GET",
            context: this,
            data: variables,
            success: function(data) {
                this.dialogPopup = vex.open({
                    contentClassName: 'resource-popup',
                    afterOpen: function($vexContent) {
                        return $vexContent.append(data);
                    }
                });
            }
        });
    },

    selectResource: function(resourceID, title, url, embedURL, isGoogle, externalID)
    {
        this.close();
        jQuery("#" + this.nothingSelectedDisplay).hide();
        jQuery("#" + this.titleDisplay + "Title").text(title);
        jQuery("#" + this.titleDisplay).show();
        jQuery("#" + this.idField).val(resourceID);
        jQuery("#" + this.idField).trigger("change");
        jQuery("#" + this.urlField).val(embedURL);
        jQuery("#" + this.urlField).trigger("change");
    },

    clearValue : function(event,ui)
    {
        jQuery("#" + this.titleDisplay).hide();
        jQuery("#" + this.nothingSelectedDisplay).show();
        jQuery("#" + this.idField).val(null);
        jQuery("#" + this.idField).trigger("change");
        jQuery("#" + this.urlField).val(null);
        jQuery("#" + this.urlField).trigger("change");
    },

    cancelImage: function()
    {
        this.close();
    },

    close: function()
    {
        vex.close(this.dialogPopup.data().vex.id);
    },

    __classvars__: {
        findModule : function(start)
        {
            var result = sl.findDataInParent(start, "resourceField");
            return result;
        }
    }
});
