var DropFileFieldSelector = Class.$extend({
    __init__: function (formDataID, fieldName, groupID, fieldID, clearLinkID, urlField, titleDisplay, nothingSelectedDisplay)
    {
        this.formDataID = formDataID;
        this.fieldName = fieldName;
        this.groupID = groupID;
        this.fieldID = fieldID;
        this.clearLinkID = clearLinkID;
        this.urlField = urlField;
        this.titleDisplay = titleDisplay;
        this.nothingSelectedDisplay = nothingSelectedDisplay;
        jQuery("#" + fieldID).data("dropfileField",this);
        jQuery("#" + clearLinkID).on("click", jQuery.proxy(this.clearValue, this));

        jQuery("#" + fieldID).find(".jsUploadButton").fileupload({
             url:"/pf4/block_document/dropFile?http_link_form_data=" + this.formDataID + "&field_name=" + this.fieldName + "&group_id=" + this.groupID,
             sequentialUploads: true,
             maxFileSize: 70000000,
             done: jQuery.proxy(this.fileDropDoneHandler, this),
             dropZone:"#" + this.fieldID
         });

        sl.log("field id","#" + this.fieldID)

         var dropEffectsOptions = {
             dropzoneElement : jQuery("#" + this.fieldID),
             messageClass : "jsDropFileMessage"
         };
         var dropzoneEffect = new DropzoneEffect(dropEffectsOptions);

    },

    fileDropDoneHandler: function (e, data) {
        e.preventDefault();
        var response = data._response.result;
        if ( (response !=  null) && (response == "err") )
        {
            sl.alert("There was a problem uploading the file.  The file may have a virus");
        }
        else
        {
            var response = JSON.parse(response);
            jQuery("#" + this.nothingSelectedDisplay).hide();
            jQuery("#" + this.titleDisplay + "Title").text(response.title);
            jQuery("#" + this.titleDisplay).show();
            jQuery("#" + this.urlField).val(response.url);
            jQuery("#" + this.urlField).trigger("change");
        }
    },

    clearValue : function(event,ui)
    {
        jQuery("#" + this.titleDisplay).hide();
        jQuery("#" + this.nothingSelectedDisplay).show();
        jQuery("#" + this.urlField).val(null);
        jQuery("#" + this.urlField).trigger("change");
        var url = "/pf4/block_document/clearFile";
        var variables = {};
        variables["http_link_form_data"] = this.formDataID;
        variables["field_name"] = this.fieldName;
        variables["group_id"] = this.groupID;
        sl.addAjaxScope(variables);
        jQuery.ajax({
            url: url,
            type: "GET",
            data: variables
        });
    },

    __classvars__: {
        findModule : function(start)
        {
            var result = sl.findDataInParent(start, "dropfileField");
            return result;
        }
    }
});
