var FolderFieldSelector = Class.$extend({
    __init__: function (fieldID, clickableID, popupURL, idField, titleDisplay, initialValue)
    {
        this.fieldID = fieldID;
        this.clickableID = clickableID;
        this.popupURL = popupURL;
        this.idField = idField;
        this.titleDisplay = titleDisplay;
        this.dialogPopupDiv = "#" + fieldID + "Dialog";
        this.dialogPopupContent = "#" + fieldID + "DialogContent"
        this.setFolderID(initialValue);
        jQuery("#" + fieldID).data("folder_field",this);
        jQuery("#" + clickableID).on("click", jQuery.proxy(this.openPopup, this));
        jQuery("#d_" + fieldID).on("click",".jsRemoveFolderLink", jQuery.proxy(this.clearValue,this));
    },

    setFolderID: function(folderID)
    {
        this.folderID = folderID;
        jQuery("#" + this.idField).val(folderID);
        if ( folderID != null )
        {
            jQuery(".jsRemoveFolderLink").show();
        }
        else
        {
            jQuery(".jsRemoveFolderLink").hide();
        }
    },

    // open popup pass object name into url
    openPopup: function(event)
    {
        window[this.fieldID] = this; //
        var variables = {};
        variables["object_name"] = this.fieldID;
        sl.addAjaxScope(variables);
        jQuery.ajax({
            url: this.popupURL,
            type: "GET",
            context: this,
            data: variables,
            success: function(data) {
                this.dialogPopup = vex.open({
                    contentClassName: 'folder-popup',
                    afterOpen: function($vexContent) {
                        return $vexContent.append(data);
                    }
                });
            }
        });
    },

    selectFolder: function(resourceID, title)
    {
        this.close();
        this.setFolderID(resourceID);
        this.toggleInstructions();
        jQuery("#" + this.titleDisplay).text(title);
    },

    clearValue : function(event,ui)
    {
        this.setFolderID(null);
        this.toggleInstructions();
        jQuery("#" + this.titleDisplay).text("");
    },

    cancel: function()
    {
        this.close();
    },

    close: function()
    {
        vex.close(this.dialogPopup.data().vex.id);
    },

    toggleInstructions: function(){
        jQuery("#d_" + this.fieldID).toggleClass("jsFolderSelected_yes jsFolderSelected_no");
    },

    __classvars__: {
        findModule : function(start)
        {
            var result = sl.findDataInParent(start, "resourceField");
            return result;
        }
    }
});
