var SectionSelectorField = Class.$extend({
    __init__: function (fieldID, initialSelection)
    {
        this.fieldID = fieldID;
        this.selection = initialSelection;
        jQuery("#val_" + this.fieldID).val(initialSelection);
        jQuery("#section_" + initialSelection).show();
        jQuery("#link_" + initialSelection).toggleClass("on");
        jQuery("#" + this.fieldID).on("click", ".jsSectionSelectorLink", jQuery.proxy(this.clickHandler, this));
    },

    clickHandler: function(event)
    {
        var id = jQuery(event.target).data("js_id");
        jQuery("#section_" + this.selection).hide();
        jQuery("#link_" + this.selection).toggleClass("on");
        jQuery("#section_" + id).show();
        jQuery("#link_" + id).toggleClass("on");
        this.selection = id;
        jQuery("#val_" + this.fieldID).val(id);
    }
});
