/*globals CKEDITOR,sl, DropzoneEffect, ProgressBar */


var RichTextEditor = Class.$extend({
    __init__: function (defaults, options) {
        this.settings = jQuery.extend(defaults, options);
        this.id = this.settings.id;
        this.jsModule = jQuery("#" + this.id);
        this.hiddenFieldID = this.settings.hiddenFieldID;
        this.customConfig = this.settings.customConfig;
        this.styleSheet = this.settings.styleSheet;
        this.toolbar = this.settings.toolbar;
        this.customConfig = this.settings.customConfig;
        this.toolbarExternalMoreButton = this.settings.toolbarExternalMoreButton;
        this.placeholderText = this.settings.placeholderText;
        jQuery("#" + this.id).data("rich_text_editor", this);
    },

    initDropArea: function () {
        this.fileDropFallback = null;
        this.editModuleArea = this.jsModule.closest(".jsTextAreaEnclosingDiv");
        this.jsDropZone = this.editModuleArea.closest(".edit-area");
        this.defaultImageStyle = "width:100%; margin: 10px 0px;";
        this.jsProgressItemTemplate = this.editModuleArea.find(".attached-item");
        this.jsProgressItemHolder = this.editModuleArea.find(".jsImageProgressBarsHolder");
        this.uploadsInt = null;
        var data = {return_json: true};
        this.initProgressBar();

        this.jsDropZone.fileupload({
            dataType: 'json',
            formData: data,
            url: "/locker2/uploadImageFile",
            sequentialUploads: true,
            maxFileSize: 70000000,
            dropZone: this.jsDropZone,
            add: jQuery.proxy(this.addHandler, this),
            progress: jQuery.proxy(this.progressInstance.progressHandler, this.progressInstance),
            done: jQuery.proxy(this.doneHandler, this)
        });

        var dropEffectsOptions = {
            dropzoneElement: this.jsDropZone,
            messageClass: "jsEditorMessage",
            findDropZone: function (dropZone) {
                return dropZone;
            }
        };
        var dropzoneEffect = new DropzoneEffect(dropEffectsOptions);
    },

    initCKEditor: function () {
        //Done by sub-class
    },

    setFileDropFallback: function (fallback) {
        this.fileDropFallback = fallback;
    },

    initProgressBar: function () {
        var options = {
            itemTemplate: this.jsProgressItemTemplate,
            itemsContainer: this.jsProgressItemHolder
        };
        this.progressInstance = new ProgressBar(options);
    },

    addHandler: function (e, data) {
        var uploadFile = data.files[0];
        if (sl.verifyFileType(uploadFile) == false) {
            return false;
        }

        var imageFileTypes = (/\.(gif|jpg|jpeg|png)$/i);
        if (imageFileTypes.test(uploadFile.name)) {
            this.submitImage(data);
        } else {
            if (this.fileDropFallback != null) {
                this.fileDropFallback.fileAddHandler(uploadFile);
            } else {
                sl.alert("Sorry, you can only drop image files into the editor.")
                return false;
            }
        }
    },

    submitImage: function (data) {
        this.jsProgressItemHolder.addClass('jsProcessing');
        this.uploadsInt++;
        this.progressInstance.addHandler(null, data);
        if (!CKEDITOR.currentInstance) {
            CKEDITOR.instances[this.id].focus();
        }
    },

    doneHandler: function (e, data) {
        this.progressInstance.doneHandler(e, data);
        this.insertImage(e, data);
        this.uploadsInt--;
        if (this.uploadsInt == 0) {
            this.jsProgressItemHolder.removeClass('jsProcessing');
        }
    },

    insertImage: function (e, data) {
        var src = "/uimg" + data.result["url"] + "?" + new Date().getTime(); // avoid browser-cached image;
        var id = data.result["id"];
        var alt = data.files[0]["name"];
        var editor = CKEDITOR.instances[this.id];
        var imageElement = editor.document.createElement('img');
        imageElement.setAttribute('src', src);
        imageElement.setAttribute('id', id);
        imageElement.setAttribute('alt', alt);
        imageElement.setAttribute('style', this.defaultImageStyle);
        editor.insertElement(imageElement);
    },

    __classvars__: {
        findRichTextEditor: function (jQueryTextArea) {
            var result = jQueryTextArea.data("rich_text_editor");
            return result;
        }
    }
});

var RichTextEditorFixed = RichTextEditor.$extend({
    __init__: function (options) {
        sl.log("RichTextEditorFixed init options", options);
        var defaults = {
            toolbar: "GenericContent",
            width: "100%",
            height: 300,
            autoGrow_minHeight: 300,
            toolbarCanCollapse: true,
            toolbarExternalMoreButton: null
        };
        this.$super(defaults, options);
        this.initDropArea();
        this.initCKEditor();
    },
    initCKEditor: function () {
        jQuery("#" + this.id).ckeditor(this.settings);//options
    }
});

var RichTextEditorInline = RichTextEditor.$extend({
    __init__: function (options) {
        sl.log("RichTextEditorInline init options", options);
        var defaults = {
            toolbar: "Shared",
            toolbarMoreToolsToolbar: "jsMoreToolsToolbar"//,
            //placeholder: "Enter text here"
        };
        this.$super(defaults, options);
        CKEDITOR.disableAutoInline = true;
        if (this.hiddenFieldID != null) {
            this.initCKEditor();
            var form = jQuery("#" + this.id).closest("form");
            jQuery(form).on("submit", jQuery.proxy(this.submitHandler, this));
        }
        if (options["sharedSpaces"] != null) {
            var toolbarID = options["sharedSpaces"]["top"];
            if (toolbarID != null) {
                jQuery("#" + toolbarID).show();
            }
        }
        CKEDITOR.on('dialogDefinition', function (ev) {
            // Take the dialog name and its definition from the event data.
            var dialogName = ev.data.name;
            var dialogDefinition = ev.data.definition;
            if (dialogName == 'cellProperties') {
                var infoTab = dialogDefinition.getContents( 'info' );
                infoTab.remove('bgColor')
                infoTab.remove('bgColorChoose')
                infoTab.remove('borderColor');
                infoTab.remove('borderColorChoose')
            }
        });


    },

    activateEditor: function (id, newSettings) {
        if (CKEDITOR.instances[id] != null) {
            this.deactivateEditor(id);
        }
        if ((this.id == null) || (this.id != id)) {
            this.id = id;
            this.initCKEditor(newSettings);
        }
    },

    deactivateEditor: function (id) {
        CKEDITOR.instances[id].destroy(true);
        this.id = null;
    },

    initCKEditor: function (newSettings) {
        CKEDITOR.cms_version = "two";
        if (this.id != null) {
            var settings = (newSettings != null) ? jQuery.extend(this.settings,newSettings)  : this.settings;
            this.editor = CKEDITOR.inline(this.id, settings);
        }
    },

    submitHandler: function () {
        var value = CKEDITOR.instances[this.id].getData();
        jQuery("#" + this.hiddenFieldID).val(value);
    },

    getValue: function () {
        var value = CKEDITOR.instances[this.id].getData();
        return value;
    },

    getCKEditor: function () {
        return CKEDITOR.instances[this.id];
    }
});
