var formUtil = new Object();

formUtil.setFieldValue = function(id, newValue)
{
    var field = jQuery(id);
    var currentValue = field.val();
    if ((currentValue == null) || (currentValue.length == 0))
    {
        field.val(newValue);
    }
    else
    {
        var newField = field.clone();
        var name = field.attr("name");
        var id = field.attr("id");
        newField.attr("id", id + "_multiplevalue");
        newField.attr("name", name);
        newField.val(newValue);
        var fieldset = field.parent("fieldset");
        fieldset.append(newField);
    }
}

formUtil.initOptionalFields = function()
{
    jQuery(".form-optional-toggle").click(function(event)
    {
        if (event.formOptionalToggle != true)
        {
            var id = jQuery(this).attr("id");
            var showDiv = jQuery("#" + id + "_show");
            var hideDiv = jQuery("#" + id + "_hide");
            jQuery(showDiv).toggle();
            jQuery(hideDiv).slideToggle("normal", function()
            {
                jQuery("input, textarea", this).first().focus();
            });
            event.formOptionalToggle = true;
        }
        return false;
    });
}

formUtil.formatFileSize = function(bytes, unit)
{
    if (bytes < (unit = unit || 1000))
    {
        return bytes + " Bytes";
    }
    var exp = Math.floor(Math.log(bytes) / Math.log(unit));
    var pre = ' ' + (unit === 1000 ? "kMGTPE" : "KMGTPE").charAt(exp - 1) + (unit === 1000 ? "" : "i") + 'B';
    return (bytes / Math.pow(unit, exp)).toFixed(1) + pre;
}
