var fileUpload = Class.$extend({
    __init__: function (options) {
        sl.log("initi fileUpload",options)
        this.dataID = options.formDataID;
        this.selectButton = jQuery("#"+options.elementId);
        this.uploadUrl = options.submitURL;

        this.formData = {
            donotupdate:"true",
            event:"upload",
            form_data_id:this.dataID
        };
        this.initFileDrop();

        jQuery(document).on("click",".jsFileSubmitButton", jQuery.proxy(this.submitHandler,this));

    },

    initProgressBar: function()
    {
        var options = {
            itemTemplate : this.jsItemTemplate,
            itemsContainer: this.attachmentProgressArea
        }
        this.progressInstance = new ProgressBar(options);
    },

    initFileDrop: function(){

        var url = this.uploadUrl;
        var formDataUpdate = this.formData;
        formDataUpdate.event = "upload";

        sl.log("url",url);

        this.selectButton.fileupload({
            formData:formDataUpdate,
            url: url,
            add: jQuery.proxy(this.addHandler, this),
//            progress: jQuery.proxy(this.progressInstance.progressHandler, this.progressInstance),
            done:  jQuery.proxy(this.doneHandler, this),
            error: jQuery.proxy(this.errorHandler, this)
        });
    },

    addHandler: function(e,data){
        var uploadFile = data.files[0];
        sl.log("uploadFile",uploadFile);

        data.submit();
    },
    doneHandler: function(e,data){
        sl.log("done uploading")
    },
    errorHandler: function(){
        sl.log("errorHandler")
    },

    submitHandler: function(e,data){
        sl.log("submit",e,data);
        this.formData["event"] = "submit";
        var url = this.uploadUrl;
        var formDataUpdate = this.formData;
                  sl.log("url submitting to",url,"with data",formDataUpdate)
        jQuery.ajax({
            url:url,
            data: formDataUpdate,
            success:jQuery.proxy(this.submitDone,this)
        })
    },

    submitDone: function(){
        sl.log("submitDone")
    }

})
