var CSSTextStyleField = Class.$extend({
    __init__: function (sharedThemeDataID,formDataID, fieldID, checkboxID, textboxID, exampleID, exampleUpdateID) {
        this.sharedThemeDataID = sharedThemeDataID;
        this.formDataID = formDataID;
        this.fieldID = fieldID;
        this.checkboxID = checkboxID;
        this.checkbox = jQuery("#" + this.checkboxID);
        this.textboxID = textboxID;
        this.textbox = jQuery("#" + this.textboxID)
        this.exampleID = exampleID;
        this.exampleUpdateID = exampleUpdateID;
        this.textboxUpdater = null;
        jQuery(this.checkbox).change(jQuery.proxy(this.updateCheckbox, this));
        jQuery(this.textbox).change(jQuery.proxy(this.updateTextbox, this));
        jQuery(this.textbox).keydown(jQuery.proxy(this.updateTextbox, this));
        jQuery("#" + this.exampleUpdateID).click(jQuery.proxy(this.updateExample,this));
        this.updateExample();
    },

    updateCheckbox: function () {
        this.updateField(this.checkbox);
        var checked = this.checkbox.prop('checked');
        if (checked) {
            jQuery("#" + this.exampleID).css("background-color", "black");
        }
        else {
            jQuery("#" + this.exampleID).css("background-color", "");
        }
    },

    updateTextbox: function(){
        if ( this.textboxUpdater == null )
        {
            this.textboxUpdater = setTimeout(jQuery.proxy(this.updateTextboxTimeoutHandler,this),500);
        }
    },

    updateTextboxTimeoutHandler: function()
    {
        this.textboxUpdater = null;
        this.updateField(this.textbox);
    },

    updateExample: function()
    {
        /*
            var css = this.textbox.val();
            jQuery("#" + this.exampleID).attr("style", css);
        */
        var variables = {};
        variables["shared_theme_data"] = this.sharedThemeDataID;
        variables["mod"] = "theme_text_styles";
        sl.addAjaxScope(variables);
        jQuery.ajax({
            url: "/pf4/cms2_sl/theme_text_styles",
            type: "GET",
            data: variables,
            context: this,
            success: function(data)
            {
                var newExample = jQuery(data).find("#style_"+this.textboxID);
                jQuery("#" + this.exampleID).html(newExample);
            }
        });
    },

    updateField: function(field)
    {
        var name = field.attr("id");
        var value = field.val();
        var variables = {};
        variables["form_data_id"] = this.formDataID;
        variables["field_name"] = name;
        variables["field_value"] = value;
        jQuery.ajax({
            url: "/pf4/support/saveField",
            type: "POST",
            data: variables,
            context: this,
            success: function(data)
            {
                if ( (data != null) && (data.lastIndexOf("err", 0) != -1) )
                {
                    window.location = sl.getTimeoutURL();
                }
            },
            error: function()
            {
                window.location = sl.getTimeoutURL();
            }
        });
    }
});
