var ProgressBar = Class.$extend({
    __init__: function (options) {
        sl.log("ProgressBar init", options);

        this.context = options.itemsContainer;
        this.fileStatusMap = {};
        this.itemTemplate = function () {
            return jQuery(options.itemTemplate.clone().attr("id", ""))
        };

        this.itemTemplateClass = "null";
        if (this.itemTemplate().hasClass("attached-item")) {
            this.itemTemplateClass = "attached-item";
        } else {
            this.itemTemplateClass = "jsItemTemplate";
        }
        this.requirementsCheck();
        this.initButtonsHandlers();
    },

    requirementsCheck: function () {
        var itemTemplate = this.itemTemplate()
        var errorText = "";
        if (!itemTemplate.hasClass("attached-item") && !itemTemplate.hasClass("jsItemTemplate")) {
            errorText = "Missing class attached-item in item template";
        }

        if (itemTemplate.find(".jsSize, .jsFileName, .jsActionButton, .jsProgressbar").length != 4) {
            errorText = "Item template is missing one or more elements with class jsFileName jsSize jsProgressbar jsActionButton ";
        }
        if (errorText != "") {
            sl.alert("Error: " + errorText);
        }
    },

    initButtonsHandlers: function () {
        this.context.on({
            click: jQuery.proxy(this.actionHandler, this)
        }, ".jsActionButton");
    },

    actionHandler: function (ev, data) {
        sl.log("actionHandler,ev,data", ev);
        var template = jQuery(ev.currentTarget).closest('.' + this.itemTemplateClass);
        var action = template.data("action");
        if (action == "abort") {
            this.abortHandler(template);
        }
        else if (action == "remove") {
            this.removeHandler(template);
        }
    },
    abortHandler: function (template) {
        var data = template.data("data");
        data.abort();
        var name = data.files[0].name
        var display = this.getProgressBarDisplay(name, data);
        display.fadeOut();
        delete this.fileStatusMap[name];
        this.context.trigger("abort", data)
    },

    abortImageForSizeHandler: function (template, type) {
        var data = template.data("data");
        data.abort();
        var name = data.files[0].name;
        if (type == "multiple-image"){
            var display = this.getProgressBarDisplay(name, data);
            display.css("opacity",.8).after("Oops!  That photo is too large.  Learn about requirements.");
        }
        else {
            var messageHtml = "Oops!  That photo is too large.  Learn about requirements.";
            var dialogData = {
                contentClassName: "sl-dialog-medium",
                html: messageHtml,
                buttons:[
                    jQuery.extend({}, vex.dialog.buttons.YES, {
                        text: 'OK'
                    })]
            };
            var dialog = new Dialog(dialogData);
            dialog.createDialogWithHtml();
        }

        delete this.fileStatusMap[name];
        sl.log("abortHandler data", data, data.formData)
        this.context.trigger("abort", data)
    },


    cancellAll: function () {
        sl.log("cancellAll")
        this.context.find(".jsActionButton").click()
    },
    removeHandler: function (template) {
        template.remove();
    },

    getProgressBarDisplay: function (fileName, data) {
        var result = this.fileStatusMap[fileName];
        if (result == null) {
            this.fileStatusMap[fileName] = this._renderUpload(data.files)    //this sets fileStatusMap
                .data('data', data)
                .data("action", "abort");
            result = this.fileStatusMap[fileName];
            result.submitted = new Date().getTime();
            this.context.addClass("sl-cms2-upload-progress-holder--active")
                .append(result);
            if (this.context.hasClass("display-none-important")) {
                this.context.removeClass("display-none-important")
            }
        }
        return result;
    },
    isAllFinished: function (e, data) {
        sl.log("isAllFinished fileStatusMap", this.fileStatusMap);
        var result = true;
        var hasOwn = Object.prototype.hasOwnProperty;
        var keys = [], name;
        for (name in this.fileStatusMap) {
            if (hasOwn.call(this.fileStatusMap, name)) {
                sl.log("name", name);
                sl.log("this.fileStatusMap[name]", this.fileStatusMap[name]);
                keys.push(name);
                if (this.fileStatusMap[name].data().data && this.fileStatusMap[name].data().data.state() != "resolved") {
                    result = false;
                    break;
                }
            }
        }
        return result;
    },
    addHandler: function (e, data, type) {
        sl.log("addHandler in progressbar")
        var display = this.getProgressBarDisplay(data.files[0].name, data);
        //the processDone is firing before all the   getProgressBarDisplay have made the map.****
        data.process().done(jQuery.proxy(this.processDoneHandler, this, e, data, type))
    },
    processDoneHandler: function (e, data, type) {
        if ((type == "image" || type == "image-multiple") && (data.files[0].size > 540000)) {
            this.abortImageForSizeHandler(this.fileStatusMap[data.files[0].name]);
        } else {
            data.submit() // This actually does the submitting
        }
    },
    progressHandler: function (e, data) {
        var display = this.getProgressBarDisplay(data.files[0].name, data);
        var progress = Math.floor(data.loaded / data.total * 100);
        sl.log("progressHandler in progressbar", data.files[0]["name"], data)
        sl.log("progress", progress);
        var progressBar = display.find('.jsProgressbar');
        progressBar.progressbar();
        progressBar.progressbar("option", "value", progress);
        var progress = data.loaded / data.total;
        var timeSpent = new Date().getTime() - display.submitted;
        var secondsRemaining = Math.round(((timeSpent / progress) - timeSpent) / 1000);
        display.find(".jsTimeRemaining").text(this._formatTime(secondsRemaining));
    },

    _renderUpload: function (files) {
        sl.log("render Upload", files)
        return this._renderTemplate(files);
    },
    _renderTemplate: function (files) {
        var result = this.renderItem({
            files: files,
            options: this.options
        });
        return result;
    },
    renderItem: function (itemDetails) {
        var item = this.itemTemplate();
        item.find(".jsFileName").text(itemDetails.files[0].name);
        var file_size = (itemDetails.files[0].size) ? this.formatFileSize(itemDetails.files[0].size) : "";
        item.find(".jsSize").text(file_size);
        return item;
    },

    doneHandler: function (ev, data) {
        sl.log("doneHandler in progress bar")
        var name = data.files[0].name;
        var display = this.getProgressBarDisplay(name, data);
        display.remove();
        delete this.fileStatusMap[name];
    },

    formatFileSize: function (bytes, unit) {
        if (bytes < (unit = unit || 1000)) {
            return bytes + " Bytes";
        }
        var exp = Math.floor(Math.log(bytes) / Math.log(unit));
        var pre = ' ' + (unit === 1000 ? "kMGTPE" : "KMGTPE").charAt(exp - 1) + (unit === 1000 ? "" : "i") + 'B';
        return (bytes / Math.pow(unit, exp)).toFixed(1) + pre;
    },

    _formatBitrate: function (bits) {
        if (typeof bits !== 'number') {
            return '';
        }
        if (bits >= 1000000000) {
            return (bits / 1000000000).toFixed(2) + ' Gbit/s';
        }
        if (bits >= 1000000) {
            return (bits / 1000000).toFixed(2) + ' Mbit/s';
        }
        if (bits >= 1000) {
            return (bits / 1000).toFixed(2) + ' kbit/s';
        }
        return bits.toFixed(2) + ' bit/s';
    },

    _formatTime: function (seconds) {
        var date = new Date(seconds * 1000),
            days = Math.floor(seconds / 86400);
        days = days ? days + 'd ' : '';
        return days +
            ('0' + date.getUTCHours()).slice(-2) + 'hours ' +
            ('0' + date.getUTCMinutes()).slice(-2) + 'minutes ' +
            ('0' + date.getUTCSeconds()).slice(-2) + "secs";
    },

    _formatPercentage: function (floatValue) {
        return (floatValue * 100).toFixed(2) + ' %';
    }

})


