var FileSelector = Class.$extend({
    __init__: function (embedURL)
    {
        sl.log("FileSelector  __init__")
        var iframe = jQuery('iframe');
        this.embedURL = embedURL;
        FileSelector.initImageHolderResize(iframe.width(), iframe.height());
        FileSelector.pageInstance = this;
        this.select_button = jQuery("#select_button");
        this.select_button.on("click", FileSelector.selectButtonClickHandler);
    },


    // on ready
    onReady : function () {
        sl.log ("FileSelector.onReady___________");
    }, //end

    __classvars__ :
    {
        findModule: function()
        {
            return FileSelector.pageInstance;
        },

        selectButtonClickHandler: function()
        {
            FileSelector.SelectFile();
        },

        initImageHolderResize : function(width, height)
        {
            sl.log("resize:", width, height);
            var iframe = jQuery('iframe');
            if (iframe != null)
            {
                jQuery('#width').val(width);
                jQuery('#height').val(height);
                sl.log("resize 1:", width, height);

                var imageHolder = jQuery('.image-holder');
                width = parseInt(width);
                height = parseInt(height);
                imageHolder.width(width);
                imageHolder.height(height);

                imageHolder.resizable({
                    aspectRatio: false,
                    alsoResize: 'iframe',
                    resize: function(event, ui) {
                        var width = iframe.width();
                        var height = iframe.height();

                        sl.log("resize 2:", width, height);
                        jQuery('#width').val(width);
                        jQuery('#height').val(height);
                    }
                });

                jQuery('#width').change(function() {
                    var width = jQuery(this).val();
                    sl.log("resize 3:", width, height);
                    if(width > 0) {
                        FileSelector.resizeIFrame(width, height);
                    }
                });

                jQuery('#height').change(function() {
                    var height = jQuery(this).val();
                    //var width = Math.ceil(height * ratio);
                    //jQuery('#width').val(width);
                    if(height > 0) {
                        FileSelector.resizeIFrame(width, height);
                    }
                });
            }
        },

        resizeIFrame : function (width, height) {
            sl.log("resize:", width, height);
            var iframe = jQuery('iframe');
            iframe.width(width);
            iframe.height(height);

            var image = jQuery('.image-holder');
            width = parseInt(width);
            height = parseInt(height);
            image.width(width);
            image.height(height);
        },

        genericAlert : function (el) {
            title = el.data("title");
            message = el
            sl.log("ERROR:", title, message);
        },

        SelectFile : function() {
            var jEditor = jQuery('#LoopFiles', window.parent.document)
            var editorInstance = jEditor.attr("data-editor")
            var height = jQuery('#height').val();
            var width = jQuery('#width').val();
            var style = "width:" + width + "px; height:" + height + "px;";

            var CKEDITOR = window.parent.CKEDITOR;
            var editor = CKEDITOR.instances[editorInstance];
            var iframeElement = editor.document.createElement( 'iframe' );
            iframeElement.setAttribute( 'src', FileSelector.pageInstance.embedURL);
            iframeElement.setAttribute( 'style', style );
            if(CKEDITOR.selectedElement && CKEDITOR.selection) {
                CKEDITOR.selection.selectElement(CKEDITOR.selectedElement);
            }
            editor.insertElement( iframeElement );
            editor.insertHtml( "
");

            CKEDITOR.loopDialog.dialog("destroy");
        }
    }
});


