var EmbedDocumentPreview = Class.$extend({
    __init__: function(previewURL, urlField, iFrameID, iFrameDivID)
    {
        this.previewURL = previewURL;
        this.urlField = urlField;
        this.iFrameID = iFrameID;
        this.iFrameDivID = iFrameDivID;
        jQuery("#" + this.urlField).change(jQuery.proxy(this.urlChangeHandler,this));
        if ( this.previewURL != null )
        {
            this.showPreview(this.previewURL);
        }
        else
        {
            var url = jQuery("#" + this.urlField).val();
            if ( (url != null) && (url.length > 0) )
            {
                this.showPreview(url);
            }
        }
    },

    urlChangeHandler : function(event)
    {
        var url = jQuery("#" + this.urlField).val();
        if ( (url != null) && (url.length > 0) )
        {
            this.showPreview(url);
        }
        else
        {
            jQuery("#" + this.iFrameDivID).hide();
            var iframe = jQuery("#" + this.iFrameID);
            iframe.attr("src",null);
        }
    },

    showPreview:    function(url)
    {
        jQuery("#" + this.iFrameDivID).show();
        url = encodeURI(url);
        var iframe = jQuery("#" + this.iFrameID);
        var src = "https://docs.google.com/viewer?url=" + url + "&embedded=true";
        iframe.attr("src",src);
    }
});


