var BaseEditHandler = BlockBase.$extend({
    __init__: function (options) {
        this.moduleID = options.moduleID;
        this.moduleElement = jQuery("#" + this.moduleID)
        this.blockID = options.blockID;
        this.blockElement = jQuery("#block_" + this.blockID);
        this.blockElement.data("jsEditorHandler", this);
        this.blockElement.data("editHandlerIniter", null);
        this.displayName = options.displayName;
        this.moduleToolbarID = options.moduleToolbarID;
        this.initModuleToolbar();

        this.initContextmenuToolbar();

        this.blockUpdating = false;//unused
        this.groupID = jQuery("#html-helpers").data("group-id");

        this.saveTimeoutHandler = null;
        this.croppieActive = null;

        BaseEditHandler.instanceCount++;
    },

    // interface for cms2 edit handler classes
    //api
    selectHandler: function () {
        this.addSelectedStyle();
        this.addUnselectButton();
        this.selectedToolbarDisplay();
    },
    addUnselectButton: function () {
        if (this.slideshowControllerInstance == null && this.blockElement.find(".jsDeselectModule").length == 0) {
            var deselectHolder = jQuery('
');
            this.blockElement.prepend(deselectHolder);
        }
    },
    //api
    addSelectedStyle: function () {
        this.blockElement.addClass("cms2-selectable-module--selected jsSelectedModule");
        this.unhoverBlock();
        if (this.isBlockMoveable(this.blockElement)) {
            var jsMoveHandle = jQuery('
' +
                '● ● '
                + this.displayName + ' ● ●')
                .data("id", this.blockElement.attr("jsid"));
            this.blockElement.append(jsMoveHandle);
            this.applyDraggable(jsMoveHandle);
        } else {
            var jsMoveHandle = jQuery('
'
                + this.displayName + '');
            this.blockElement.append(jsMoveHandle);
        }

    },

    unhoverBlock: function () {
        this.blockElement.find(".jsHoverElement").remove();
    },

    isBlockMoveable: function () {
        var result = true;
        if (this.blockElement.hasClass("jsFixedModule")) {
            result = false;
        }
        return result;
    },

    setBlockUpdating: function (boolean) {   //unused atm
        this.blockUpdating = boolean;
    },

    getBlockUpdating: function () {
        return this.blockUpdating;
    },

    getBlockID: function () {
        return this.blockID;
    },

    getBlockElement: function () {
        return this.blockElement;
    },

    //api
    saveHandler: function (callBack) {
        if (callBack != null) {
            callBack();
        }
    },

    editHandler: function () {

    },

    justAddedHandler: function () {

    },

    //api
    deselectHandler: function () {
        sl.log("deselectHandler api")
        this.blockElement.removeClass("cms2-selectable-module--selected");
        this.blockElement.find(".jsModuleHandle, .jsDeselectModule").remove();
        this.deselectedToolbarDisplay();
        this.blockElement.trigger("jsModuleDeselected");
    },

    getSlideshowInstance: function () {
        return this.slideshowControllerInstance;
    },

    deselectButtonClickHandler: function () {
        // debugger;
        if (this.slideshowControllerInstance != null) {
            this.slideshowControllerInstance.update();
        } else {
            var editor = EditCMS2Page.findModule();
            editor.saveModule(this.blockID);
            this.removeToolbar();
        }
    },

    removeToolbar: function () {
        jQuery("#id-cms-toolbar").empty();
    },

    //api
    hover: function (target) {
        if (target.hasClass("jsSelectedModule") && (jQuery(target.context).hasClass("jsColumnSpaceHover") == false)) {//checking context to allow selecting the space in an empty selected navigation holder
            return;
        }
        this.unhover();
        var canAdd = this.blockElement.data("can-add");
        var height = this.blockElement.height() + 8;
        var width = this.blockElement.outerWidth() + 8;
        this.hoverElement = jQuery("
")
            .addClass("cms2-selectable-module--hover jsHoverElement")
            .height(height)
            .width(width)
            .css({
                left: "-6px",
                top: "-4px"
            });

        var dialogData = this.blockElement.data("dialog") || null;
        var belowLollipop = jQuery('
').data("dialog", dialogData);

        if (target.hasClass("jsLayoutModule")) {// for the bottom space of columns and rows
            this.blockElement.find(".jsAddBlockLink:last").addClass("cms2-selectable-module--hover__column_space__add")
        }
        else { // for ordinary modules
            var aboveLollipop = jQuery('
').data("dialog", dialogData);
            this.blockElement.children().addClass("z-index-350")
            this.blockElement.prepend(this.hoverElement)
            if (canAdd == true) {
                this.blockElement.prepend(aboveLollipop).append(belowLollipop);
            }
        }
    },

    //api
    unhover: function () {
        //, .jsSideBar
        this.blockElement.find(".sl-cms2-lollipop-add-above, .sl-cms2-lollipop-add-below, .sl-cms2-lollipop-across-bar, .jsSideBar").remove();
        this.blockElement.find(".z-index-350").removeClass("z-index-350");
        jQuery(".cms2-selectable-module--hover__column_space__add").removeClass("cms2-selectable-module--hover__column_space__add")
        if (this.hoverElement != null) {
            this.hoverElement.remove();
        }
    },

    initModuleToolbar: function () {

        var options = {
            editorHandler: this,
            moduleToolbarID: this.moduleToolbarID,
            moduleToolbarContainerID: "id-cms-toolbar",
            deleteBlock: jQuery.proxy(this.deleteBlock, this)
        }
        this.moduleToolbar = new ToolbarModule(options);
    },

    initContextmenuToolbar: function () {
        var contextmenuToolbarID = "jsContextTools_" + this.blockID;
        this.contextToolbarElement = jQuery('
')
            .addClass("sl-cms2-context-menu jsContextmenu")
            // .append(jQuery("
").attr("id","jsContextTextToolRow"))

        this.blockElement.prepend(this.contextToolbarElement);
        // debugger;
        var options = {
            editorHandler: this,
            moduleToolbarID: this.moduleToolbarID,
            moduleToolbarContainerID: contextmenuToolbarID,
            deleteBlock: jQuery.proxy(this.deleteBlock, this),
            contextMenu: true
        }
        this.contextmenuToolbar = new ToolbarModule(options);
    },

    displayContextmenuToolbar: function () {
        sl.log("displayContextmenuToolbar")
        this.contextmenuToolbar.displayToolbar();
        this.contextToolbarElement.css("z-index", 390);  // to get over 508 flag
    },

    selectedToolbarDisplay: function () {
        sl.log("selectedToolbarDisplay")
        this.moduleToolbar.displayToolbar();
    },


    deselectedToolbarDisplay: function () {
        this.moduleToolbar.undisplayToolbar();
    },

    showMenu: function (target) {
        var menu = target.find(".jsDropButton").first();
        if (menu != null) {
            menu.removeClass("display-none").addClass("display-block");
        }
    },

    hideMenus: function () {
        jQuery(".jsDropButton").removeClass("display-block").addClass("display-none");

    },

    reloadToolbar: function () {
        return false;
    },

    setDisplayName: function (displayName) {
        this.displayName = displayName;

    },

    applyDraggable: function (handle) {
        var options = {
            start: jQuery.proxy(this.draggableStartHandler, this),
            stop: jQuery.proxy(this.draggableStopHandler, this),
            helper: "clone",
            appendTo: "body",
            revert: "invalid",
            cursorAt: {left: 20, top: -15}
        };
        handle.draggable(options);
    },

    draggableStartHandler: function (event, ui) {
        var editor = EditCMS2Page.findModule();
        editor.setIsDragging(true);
        var moveDropzones = jQuery(".jsMoveDropzone");
        var i, targetModule, moveDropzone;
        for (i = 0; i < moveDropzones.length; i++) {
            var moveDropzone = jQuery(moveDropzones[i]);
            var targetModule = moveDropzone.closest("[id^=block_]");
            if (targetModule.data("can-add")) {
                targetModule.find(".jsMoveDropzone:first, .cms2-add-dropzone:first, .jsMoveDropzone:last").show();
                if (targetModule.children().last().hasClass("jsDropColumn")) {
                    targetModule.children().last().removeClass("display-none");
                    targetModule.children(".jsColumnSpaceHover").addClass("display-none-important");
                    jQuery(document).find(".sl-cms2-module-dropzone-arrow-up, .sl-cms2-module-dropzone-arrow-down").removeClass("sl-cms2-module-dropzone-arrow-up sl-cms2-module-dropzone-arrow-down");
                }
            }
        }
        this.createDragHelper(event, ui)
        this.applyDroppable(".jsMoveDropzone");
    },

    createDragHelper: function (event, ui) {
        sl.log("draggable start", event, ui, jQuery(ui));
        var targetModule = jQuery(event.target).closest(".cms2-selectable-module");
        var targetWidth = targetModule.width()
        var jsHelperClone = targetModule.clone()
            .addClass("cms2-draggedModule-helper")
            .width(targetWidth)
        ui.helper.html(jsHelperClone);
    },

    applyDroppable: function (selector) {
        var options = {
            //hoverClass: "sl-cms2-lollipop-horizontal sl-cms2-lollipop-horizontal__hover",
            tolerance: "pointer",
            greedy: true,
            drop: jQuery.proxy(this.dropAddHandler, this),
            over: jQuery.proxy(this.dropOverHandler, this),
            out: jQuery.proxy(this.dropOutHandler, this),
        };
        jQuery(selector).droppable(options)
    },

    draggableStopHandler: function () {
        var editor = EditCMS2Page.findModule();
        editor.setIsDragging(false);
        jQuery(".jsMoveDropzone").hide();
        jQuery(".cms2-add-dropzone").hide();
        jQuery(".jsDropColumn").addClass("display-none");
        jQuery(".jsColumnSpaceHover").removeClass("display-none-important");
        jQuery(".jsModuleDarkener").remove();
    },

    dropOverHandler: function (event, ui) {
        event.stopPropagation();
        var target = jQuery(event.target);
        var targetModule = this.targetModuleFromTarget(target);
        sl.log("dropOverHandler in base edit", targetModule)
        jQuery(document).find(".sl-cms2-module-dropzone-arrow-up, .sl-cms2-module-dropzone-arrow-down").removeClass("sl-cms2-module-dropzone-arrow-up sl-cms2-module-dropzone-arrow-down");
        jQuery(document).find(".jsModuleDarkener").remove();
        var darkenerElement = jQuery('
').width(targetModule.width())
        targetModule.prepend(darkenerElement);
        if (target.hasClass("jsDropAbove")) {
            targetModule.find(".jsDropAbove:first").addClass("sl-cms2-module-dropzone-arrow-up");
        } else if (target.hasClass("jsDropBelow")) {
            sl.log("target has jsDropBelow, next is ", targetModule.next(".cms2-selectable-module"))
            if (targetModule.nextAll(".cms2-selectable-module").length > 0) {   //not last in column
                if (targetModule.nextAll(".cms2-selectable-module").first().hasClass("jsLayoutModule")) {
                    sl.log("next is layout")
                    targetModule.nextAll(".cms2-selectable-module").first().find(".jsDisplayOnlyLollipop:first").addClass("sl-cms2-module-dropzone-arrow-up");
                } else {
                    sl.log("next is a module")
                    targetModule.find(".jsDropBelow:last").addClass("sl-cms2-module-dropzone-arrow-down");
                }
            } else {
                sl.log("must be end of column")
                targetModule.find(".jsDropBelow:last").addClass("sl-cms2-module-dropzone-arrow-down");
            }
        } else if (target.hasClass("jsDropColumn")) {
            sl.log("column space");
            sl.log("target", target);
            var bottom = targetModule.outerHeight();
            var i;
            var blockChildren = targetModule.children(".cms2-selectable-module")
            for (i = 0; i < blockChildren.length; i++) {
                bottom -= jQuery(blockChildren[i]).height();
            }
            darkenerElement.css({
                "height": bottom,
                "top": targetModule.height() - bottom
            });
            targetModule.closest(".jsLayoutModule").children(".cms2-move-dropzone__column:last").addClass("sl-cms2-module-dropzone-arrow-up");
        } else {
            sl.log("target not recognized targetModule", targetModule);
            return;
        }
    },

    dropOutHandler: function (event, ui) {         //currently unused
        var target = jQuery(event.target);
        var targetModule = target.closest(".cms2-selectable-module");
        jQuery(document).find(".sl-cms2-module-dropzone-arrow-up, .sl-cms2-module-dropzone-arrow-down").removeClass("sl-cms2-module-dropzone-arrow-up sl-cms2-module-dropzone-arrow-down");
        jQuery(".jsModuleDarkener").remove();
    },

    targetModuleFromTarget: function (target) {
        return (target.hasClass("jsDropColumn")) ? target.closest(".jsLayoutModule") : target.closest(".cms2-selectable-module");
    },
    dropAddHandler: function (event, ui) {
        sl.log("dropAddHandler in base edit")
        event.stopPropagation();
        var target = jQuery(event.target);
        sl.log("dropAddHandler target", target);

        var movingId = ui.draggable.data("id");
        var variables = {
            edit_form_data: this.getFormDataID(),
            block_id: movingId
        };
        var targetModule = this.targetModuleFromTarget(target);
        if (target.hasClass("jsDropAbove")) {
            variables.before_id = targetModule.attr("jsid")
        } else if (target.hasClass("jsDropColumn") || (targetModule.nextAll(".cms2-selectable-module").length == 0) && targetModule.closest(".jsLayoutModule").length > 0) {
            variables.parent_id = targetModule.closest(".jsLayoutModule").attr("jsid")
        } else if (targetModule.nextAll(".cms2-selectable-module").length > 0) {
            variables.before_id = targetModule.nextAll(".cms2-selectable-module").first().attr("jsid")
        } else {
            sl.log("target not recognized");
            return;
        }
        variables.movingId = movingId;
        sl.addAjaxScope(variables);
        if (movingId != variables.before_id && movingId != variables.parent_id) {
            var url = "/pf4/cms2/moveBlock";
            jQuery.ajax({
                url: url,
                data: variables,
                success: jQuery.proxy(this.movingBlockSuccessHandler, this, movingId, variables),
                error: function () {
                    sl.alert("There was an error moving this module");
                }
            });
        }
    },

    movingBlockSuccessHandler: function (block_id, variables) {
        var editorHandler = sl.findDataInParentWithMethod("#block_" + block_id, "jsEditorHandler", "movingChildHandler");
        if (editorHandler != null) {
            var targetId = (variables.parent_id != null) ? variables.parent_id : variables.before_id;
            editorHandler.movingChildHandler(block_id, targetId);
        } else {
            this.updatePage(block_id);
        }
    },

    saveAndDeselectActiveBlock: function (callback) {
        var cmsModule = EditCMS2Page.findModule();
        cmsModule.saveAndDeselectActiveBlock(callback);
    },

    updatePage: function (block_id) {
        var cmsModule = EditCMS2Page.findModule();
        cmsModule.updateDocument(block_id);
    },

    updateBlock: function () {
        var editor = EditCMS2Page.findModule();
        editor.updateBlock(this.blockID);
    },

    deleteBlock: function () {
        this.deselectHandler();
        EditCMS2Page.findModule().clearActiveEditableModule();
        var block_id = this.blockID;
        var url = "/pf4/cms2/deleteBlock";
        var variables = {
            edit_form_data: this.getFormDataID(),
            block_id: block_id
        };
        jQuery.ajax({
            url: url,
            data: variables,
            success: jQuery.proxy(this.deleteSuccessHandler, this, block_id)
        });
    },

    deleteSuccessHandler: function (block_id) {
        var editorHandler = sl.findDataInParentWithMethod("#block_" + block_id, "jsEditorHandler", "deletingChildHandler");
        if (editorHandler != null) {
            editorHandler.deletingChildHandler(block_id);
        } else {
            this.blockElement.animate({
                opacity: 0,
                height: '0'
            }, 500, jQuery.proxy(this.updatePage, this));
        }
    },

    copyBlock: function (title, description, categoryID) {
        var height = this.blockElement.height();
        var width = this.blockElement.width();
        this.flashBlock();
        var variables = {
            edit_form_data: this.getFormDataID(),
            block_id: this.getBlockID(),
            title: title,
            description: description,
            category_id: categoryID,
            height: height,
            width: width
        };
        sl.addAjaxScope(variables);
        jQuery.ajax({
            url: "/pf4/cms2/copyBlock",
            method: "POST",
            data: variables,
            context: this,
            success: jQuery.proxy(this.copySuccess, this)
        });
    },

    flashBlock: function () {
        this.flashElement = jQuery('
');
        this.flashElement.appendTo(this.blockElement);
        this.flashElement.one('webkitAnimationEnd oanimationend msAnimationEnd animationend', function (e) {
            jQuery(".flash").remove();
        });
    },

    copySuccess: function () {
        sl.successMessage("You've successfully copied the snippet to the snippet library!");

    },

    makeDistrictBlock: function (title) {
        // deselect active block to save it first
        this.saveAndDeselectActiveBlock(jQuery.proxy(this.doMakeDistrictBlock, this, title));
    },

    doMakeDistrictBlock: function (title) {
        var height = this.blockElement.height();
        var width = this.blockElement.width();
        var variables = {
            edit_form_data: this.getFormDataID(),
            block_id: this.getBlockID(),
            title: title,
            height: height,
            width: width
        };
        sl.addAjaxScope(variables);
        jQuery.ajax({
            url: "/pf4/cms2_admin/makeDistrictModule",
            data: variables,
            context: this,
            success: function (data) {
                this.updatePage();
            }
        });
    },


    toggleStartOfTemplate: function () {
        var variables = {
            edit_form_data: this.getFormDataID(),
            block_id: this.getBlockID()
        };
        sl.addAjaxScope(variables);
        jQuery.ajax({
            url: "/pf4/cms2/toggleBlockStartOfTemplate",
            data: variables,
            context: this,
            success: function (data) {
                if ((data != null) && (data == "true")) {
                    this.blockElement.addClass("cms2-start-of-template");
                }
                else {
                    this.blockElement.removeClass("cms2-start-of-template");
                }
            }
        });
    },

    getFileUploadData: function () {
        return {
            edit_form_data: this.getFormDataID(),
            block_id: this.blockID,
            group_id: this.groupID
        };
    },

    initUploader: function (options) { //this is the new uploader... it is fired by having the upload tool in the toolbar... must pass in fileUploadButton
        var settings = {
            blockID: this.blockID,
            blockElement: this.blockElement,
            groupID: this.groupID,
            doneHandler: jQuery.proxy(this.fileDropDoneHandler, this),

        };
        jQuery.extend(settings, options)
        this.slideshowParent = this.blockElement.closest(".jsSlideshow").closest(".cms2-selectable-module");
        this.slideshowControllerInstance = this.slideshowParent.data("jsEditorHandler"); //slideshowControllerInstance gets used elsewhere
        if (this.slideshowControllerInstance != null) {
            settings.uploadURL = "/pf4/cms2/setSlideLayoutImage";
            this.slideshowControllerInstance.initSlidehowUploader(settings); //if module in a slideshow, init uploader there
        } else {
            var uploader = new Uploader(settings);
        }
    },

    initFileUpload: function (jsUploadFileButton, uploadURL, addHandler) {    //OLD    ... still used by file list
        this.initDropzoneEffect();
        var fileUploadData = {
            edit_form_data: this.getFormDataID(),
            block_id: this.blockID,
            group_id: this.groupID
        };

        jsUploadFileButton.data("sladd", jQuery.proxy(addHandler, this));

        jsUploadFileButton.fileupload({
            url: uploadURL,
            maxFileSize: 7000000000,
            loadImageMaxFileSize: 7000000000,
            imageMaxWidth: 1920,
            imageMaxHeight: 1080,
            disableImageResize: false,
            formData: fileUploadData,
            dropZone: "#" + this.moduleID,
            error: jQuery.proxy(this.uploadErrorHandler, this),
            thumbnail: false,
            start: jQuery.proxy(this.uploadStartHandler, this),
        });
    },
    uploadStartHandler: function (e, data) {
        //EditCMS2Page.findModule().activateModule(this.blockID);
    },

    addVideoHandler: function (e, data) {
        var uploadFile = data.files[0];
        if (sl.verifyFileType(uploadFile) == false) {
            return false;
        }

        var videoFileTypes = (/\.(mp4|mov)$/i);
        if (videoFileTypes.test(uploadFile.name)) {
            this.initProgressBar();
            this.jsProgressItemHolder.show();
            this.blockElement.on({
                'fileuploaddone': jQuery.proxy(this.fileDropDoneHandler, this),
                'fileuploadprogress': jQuery.proxy(this.progressInstance.progressHandler, this.progressInstance)
            });

            this.jqXHR = this.progressInstance.addHandler(e, data);
            sl.log("upload video");
            return true;
        }
        else {
            sl.alert("Sorry, mp4 and mov are the only supported video formats.")
            return false;
        }
    },

    addCaptionsHandler: function (e, data) {
        var uploadFile = data.files[0];
        if (sl.verifyFileType(uploadFile) == false) {
            return false;
        }
        var videoFileTypes = (/\.(vtt)$/i);
        if (videoFileTypes.test(uploadFile.name)) {
            this.initProgressBar();
            this.jsProgressItemHolder.show();
            this.blockElement.on({
                'fileuploaddone': jQuery.proxy(this.fileDropDoneHandler, this),
                'fileuploadprogress': jQuery.proxy(this.progressInstance.progressHandler, this.progressInstance)
            });

            this.jqXHR = this.progressInstance.addHandler(e, data);
            this.jqXHR.error(jQuery.proxy(this.uploadErrorHandler, this));
            sl.log("upload video captions");
        }
        else {
            sl.alert("Sorry, please upload valid captions file (.vtt)")
            return false;
        }
    },

    fileDropDoneHandler: function () {
        EditCMS2Page.findModule().deselectModule();
        this.update();
    },

    update: function () {
        var editor = EditCMS2Page.findModule();
        editor.updateBlock(this.blockID);
    },

    selectFromLockerHandler: function (type) {
        var fromLockerURL = (typeof type == "string") ? "/pf4/resource/select_resource?resource_type=" + type : this.fromLockerURL;
        sl.log("selectFromLockerHandler");
        this.settingBlockImageURL();
        var objectName = "image" + this.blockID;
        window[objectName] = this;
        var variables = {};
        variables.object_name = objectName;
        variables.mod = "container-table";
        sl.addAjaxScope(variables);
        var dialogData = {
            url: fromLockerURL,
            contentClassName: "sl-dialog-wide",
            additonalData: variables
        };
        var dialog = new Dialog(dialogData);
        dialog.createDialog();
    },

    selectResource: function (resourceID, title, url, embedURL, isGoogle, externalID) {
        if (this.isSettingBlockLinkURL()) {
            this.addLinkDialog.close();
            this.addLinkDialog = null;
            this.clearSettingURLType();
            var attributes = {
                url: url,
                open_in_new_window: false
            };
            this.moduleToolbar.toolbarTools["link-advanced"].setLink(attributes);
        }
        else {   //moved video handling of select resource to video class
            this.clearSettingURLType();
            var variables = {
                edit_form_data: this.getFormDataID(),
                block_id: this.blockID,
                resource_id: resourceID,
                title: title,
                link_url: url,
                embed_url: embedURL
            };
            if (isGoogle) {
                variables.google_id = "true";
                variables.external_id = externalID;
            }
            sl.addAjaxScope(variables);
            var resize_options = {
                doneHandler: jQuery.proxy(this.update, this),
                blockElement: this.blockElement
            };
            jQuery.extend(variables, resize_options)
            var resizer = new ImageResizer(variables);
            vex.closeAll();
        }

    },

    closeDialogAndUpdate: function () {
        vex.closeAll();
        this.update();
    },

    initDropzoneEffect: function () {
        this.dragOverlayClass = "sl-cms2-attachments__dropzone";
        this.dropOverlayClass = "sl-cms2-attachments__dropzone--drop"
        var dropEffectsOptions = {
            dropzoneElement: this.moduleElement,
            messageClass: "jsImageUploadMessage",
            dragOverlayClass: this.dragOverlayClass,
            dropOverlayClass: this.dropOverlayClass
        };
        var dropzoneEffect = new DropzoneEffect(dropEffectsOptions);
    },

    initProgressBar: function () {
        this.jsProgressItemTemplate = jQuery("#html-helpers").find(".jsUploadItemTemplate").clone();
        this.jsProgressItemHolder = this.blockElement;
        var options = {
            itemTemplate: this.jsProgressItemTemplate,
            itemsContainer: this.jsProgressItemHolder
        };
        this.progressInstance = new ProgressBar(options);
    },
    uploadErrorHandler: function (jqXHR, textStatus, errorThrown) {
        if (errorThrown === 'abort') {
            this.jsProgressItemHolder.fadeOut();
        } else {
            sl.alert('An error occured during the image upload');
        }
    },

    ajaxCallAttributeSetting: function (options) {
        var url = options.url;
        var id = this.getBlockID();
        var formDataID = this.getFormDataID();
        var variables = {};
        variables["edit_form_data"] = formDataID;
        variables["block_id"] = id;
        var key;
        var attributes = options.attributes;
        for (key in attributes) {
            if (attributes.hasOwnProperty(key)) {
                variables[(options.attributePrefix || "") + key] = attributes[key];
            }
        }
        sl.addAjaxScope(variables);
        var ajaxCall = jQuery.ajax({
            type: "POST",
            context: this,
            url: url,
            data: variables,
            success: options.callback
        });

        return ajaxCall;
    },

    startAutosaveTimer: function (event) {
        if (event != null) {
            try {
                event.stopPropagation();
            }
            catch (err) {
                // this happens when ckeditor triggered the event
            }
        }
        if (this.saveTimeoutHandler == null) {
            this.saveTimeoutHandler = setTimeout(jQuery.proxy(this.autoSave, this, null), 2500);//saveHandler was 5000
        }
    },

    autoSave: function () {
        this.clearSaveTimeout();
        sl.log("autoSave " + this.moduleID);
        this.saveHandler(null);
    },

    clearSaveTimeout: function () {
        if (this.saveTimeoutHandler != null) {
            clearTimeout(this.saveTimeoutHandler)
            this.saveTimeoutHandler = null;
        }
    },

    getCustomCssClass: function () {
        return jQuery("#block_" + this.blockID).data("custom-css-class");
    },

    addClickShield: function () {
        var css;
        if (this.blockElement.find(".jsModuleTitle:first").length > 0) {
            var titleElementHeight = this.blockElement.find(".jsModuleTitle:first").height()
            css = {
                height: this.blockElement.height() - titleElementHeight,
                top: titleElementHeight,
                width: "100%",
                "z-index": 1,
                position: "absolute"
            }
        }
        else {
            css = {
                height: "100%",
                width: "100%",
                "z-index": 1,
                position: "absolute"
            }
        }
        jQuery("
").css(css)
            .appendTo(this.moduleElement)
    },
    preventImageDragging: function () {
        this.blockElement.on("dragstart", "img", false);
    },
    deleteHandler: function () {
        //e.g. for clearing timeouts
        this.clearSaveTimeout();
    },

    isActiveEditableModule: function () {
        var activeEditableModuleID = EditCMS2Page.GetActiveEditableModuleID();
        var result = (activeEditableModuleID != null) && (activeEditableModuleID == this.blockID);
        return result;
    },

    titleBlurHandler: function () {
        if (this.isActiveEditableModule()) {
            this.title = this.blockElement.find(".jsModuleTitle:first").text();
            this.saveTitle();
            if (this.moduleToolbar.toolbarTools["title-module"] != null) {    //only update toolbar field if there is one
                this.moduleToolbar.toolbarTools["title-module"].updateTitleField(this.title);
            }
            if (this.moduleToolbar.toolbarTools["subtitle"] != null) {    //only update toolbar subititle field if there is one
                var subtitle = this.blockElement.find(".jsModuleSubTitle:first").text()
                this.moduleToolbar.toolbarTools["subtitle"].updateSubTitleField(subtitle);
            }
        }
    },

    saveTitle: function (title) {
        if (title != null) {    //used for TitleSubtitle toolbar dialog callback
            this.title = title;
        }
        sl.log("saveTitle this.title", this.title)
        var options = {
            url: "/pf4/cms2/setBlockTitle",
            attributes: {title: this.title}
        };
        return this.ajaxCallAttributeSetting(options);
    },
    saveSubtitle: function (titleData) {
        var subtitle = titleData || this.blockElement.find(".jsModuleSubTitle:first").text();
        sl.log("saveSubTitle subtitle", subtitle);
        var options = {
            url: "/pf4/cms2/setBlockSubtitle",
            attributes: {subtitle: subtitle}
        };
        return this.ajaxCallAttributeSetting(options);
    },

    initPersistentTitleListener: function () {      //the persistent title doesn't disappear when its empty
        this.blockElement.find(".jsModuleTitle:first").addClass("jsPersistentTitle")
        this.basicTitleListeners()
    },

    basicTitleListeners: function () {
        this.blockElement.on({
            "keyup paste cut mouseup": jQuery.proxy(this.startAutosaveTimer, this),
            "blur": jQuery.proxy(this.titleBlurHandler, this)
        }, ".jsModuleTitle:first, .jsModuleSubTitle:first");
    },


    initTitleListener: function () {
        if (this.blockElement.find(".jsModuleTitle:first").text() != "") {
            this.blockElement.find(".jsModuleTitle:first").attr("contenteditable", true)
        }

        this.basicTitleListeners()
    },

    saveTitleHandler: function (callback) {
        this.clearSaveTimeout();
        this.mainCallback = callback;
        var titleElement = this.getModuleTitleElement();
        if (titleElement != null) {
            this.title = titleElement.text();
            sl.log("saveTitleHandler about to saveTitleHandler")
            if (this.moduleToolbar.toolbarTools["subtitle"] != null) {
                sl.log("saveTitleHandler about to saveTitleHandler2")
                this.saveSubtitle();
            }
            this.saveTitle().then(jQuery.proxy(this.mainCallback, this));
            if ((this.title == "") && (titleElement.hasClass("jsPersistentTitle") == false)) {
                titleElement.attr("contenteditable", false)
            }
        }
        else {
            this.mainCallback();
        }
    },

    getModuleTitleElement: function () {
        var result = this.blockElement.find(".jsModuleTitle:first");
        if (result != null) {
            var id = result.attr("id");
            if ((id != null) && (id != this.blockID + "_module_title")) {
                result = null;
            }
        }
        return result;
    },

    isThisAChild: function (element) {
        return (this.moduleElement.find(element).length > 0)
    },

    //requires this.imageURL to be set by module
    //currentImage is just element used for getting height and width

    cropImageActivate: function (currentImage) {
        sl.log("cropImageActivate")
        this.currentImage = currentImage;
        var currentImageOuterHeight = currentImage.outerHeight();
        var cropImage = jQuery('
').css({
            height: currentImageOuterHeight,
            width: currentImage.width(),
            "position": "absolute"
        });
        this.blockElement.css("z-index", 385);
        this.currentImage.css({"position": "relative"}).prepend(cropImage);
        this.croppieObject = cropImage.croppie({
            viewport: {
                width: currentImage.width(),
                height: currentImageOuterHeight
            },
            boundary: {
                width: currentImage.width(),
                height: currentImageOuterHeight
            },
            update: jQuery.proxy(this.croppieUpdateHandler, this)
        });
        this.croppieObject.croppie('bind', this.imageURL);
        this.fitCheckboxContainer = jQuery('#jsTemplates .sl-cms2-croppie--fit-checkbox__container').clone();
        this.croppieObject.find(".cr-slider-wrap").append(this.fitCheckboxContainer);
        this.fitCheckbox = this.fitCheckboxContainer.find(".jsFitCheckbox");
        if (currentImage.data("ratio") == "original") {
            this.fitCheckbox.prop('disabled', true);
        } else {
            this.fitCheckbox.change(jQuery.proxy(this.fitImageCheckHandler, this));
        }
        this.croppieActive = true;
    },

    fitImageCheckHandler: function (event) {

        if (this.fitCheckbox.is(":checked")) {
            sl.log("do fit");
            this.croppieObject.find(".cr-boundary").css("opacity", 0);
            this.croppieObject.find(".cr-slider").prop('disabled', true);
            this.currentImage.css({
                "background-size": "contain",
                "background-position": ""
            })
        } else {
            this.croppieObject.find(".cr-boundary").css("opacity", 1);
            this.currentImage.css({
                "background-size": "cover"
            });
            this.croppieObject.find(".cr-slider").prop('disabled', false);
        }
    },

    getVideoFitAttributes: function () {
        var attributes = {
            background_position: "-fit"
        };
        return attributes
    },
    getVideoUnFitAttributes: function () {
        var attributes = {
            background_position: ""
        };
        return attributes

    },
    getCropAttributes: function () {
        var results = this.croppieObject.croppie("get");
        sl.log("crop results", results);
        var attributes;
        if (this.fitCheckbox.is(":checked")) {
            attributes = {
                background_size: "contain",
                background_position: ""
            };
        } else {
            var origImgHeight = parseInt(results.img.height);
            var origImgWidth = parseInt(results.img.width);

            var zoomAdjust = origImgWidth / this.currentImage.width();
            var zoomPercentage = results.zoom * zoomAdjust * 100;

            var topCrop = parseInt(results.points[1]);
            var bottomCrop = origImgHeight - parseInt(results.points[3]);
            var percentShiftTop = (topCrop + bottomCrop == 0) ? 0 : topCrop / (topCrop + bottomCrop) * 100;

            var leftCrop = parseInt(results.points[0]);
            var rightCrop = origImgWidth - parseInt(results.points[2]);
            var percentShiftLeft = (leftCrop + rightCrop == 0) ? 0 : leftCrop / (leftCrop + rightCrop) * 100;

            attributes = {
                background_size: zoomPercentage + "%",
                background_position: percentShiftLeft + "% " + percentShiftTop + "%"
            };
        }
        return attributes;
    },
    destroyCroppie: function () {
        jQuery(document).off("click", "body", jQuery.proxy(this.clickOutOfCroppieHandler, this));
        this.croppieObject.remove();
        this.currentImage.css({"position": ""});
        this.croppieObject = null;
        this.croppieActive = null;
    },
    croppieUpdateHandler: function () {
        this.moduleToolbar.toolbarTools["crop-image"].setCropAttributesNoUpdate(this.getCropAttributes());
    },

    saveAndCloseCroppier: function () {
        this.moduleToolbar.toolbarTools["crop-image"].setCropAttributesNoUpdate(this.getCropAttributes());
        this.updateBlock();
    },

    isCropppieActive: function () {
        return this.croppieActive;
    },

    confirmCropHandler: function () {
        this.moduleToolbar.toolbarTools["crop-image"].setImageOnlyCropAttributes(this.getCropAttributes());
        this.destroyCroppie();
        this.update();
    },

    //The following methods are used for the link_advanced toolbar tool
    addLinkDialogHandler: function (event, id) {//id is passed in if editing
        event.stopPropagation();
        this.setEditingLinkID(id);
        this.settingBlockLinkURL();
        var variables = {};
        variables["link_id"] = id;
        variables["block_id"] = this.blockID;
        variables["edit_form_data"] = this.getFormDataID();
        sl.addAjaxScope(variables);
        var objectName = this.blockID + "_link_handler";
        window[objectName] = this;
        var dialogData = {
            url: "/pf4/cms2_site/new_block_link?mod=tab_module&object_name=" + objectName,
            dialogCssClasses: "sl-dialog-medium",
            variables: variables,
            formArea: "jsDefaultDialogContent"
        };
        this.addLinkDialog = new SLFormDialog(dialogData);
        this.addLinkDialog.open();
    },

    setEditingLinkID: function (id) {
        this.editingLinkID = id;
    },


    setEditDialog: function (data) {
        var dialogHtml = jQuery("#html-helpers").find("#jsAddEditLinkDialog").clone();
        dialogHtml.find(".jsAddEditHeading").text(dialogHtml.data("edit-title"));
        this.edittingData = data;
        if (data != null) {
            dialogHtml.find("[name=link_name]").val(this.edittingData["linkName"]);
            dialogHtml.find("[name=url]").val(this.edittingData["url"]);
            dialogHtml.find("[name=open_in_new_window]").prop("checked", this.edittingData["open"]);
        }
        this.openAddEditDialog(dialogHtml);
    },

    // called by web_link.js and select_from_site_map
    selectLink: function (title, url, openInNewWindow) {
        this.addLinkDialog.close();
        this.addLinkDialog = null;
        var attributes = {
            url: url,
            open_in_new_window: openInNewWindow
        };
        this.moduleToolbar.toolbarTools["link-advanced"].setLink(attributes);
        this.update();
    },

    // called by web_link.js if user cancels add link
    cancelAddLinkDialog: function () {
        this.update();
        this.addLinkDialog.close();
    },

    settingBlockImageURL: function () {
        this.settingURLType = "image"
    },

    isSettingBlockImageURL: function () {
        var result = (this.settingURLType != null) && (this.settingURLType == "image");
        return result;
    },

    settingBlockLinkURL: function () {
        this.settingURLType = "link"
    },

    isSettingBlockLinkURL: function () {
        var result = (this.settingURLType != null) && (this.settingURLType == "link");
        return result;
    },


    clearSettingURLType: function () {
        this.settingURLType = null;
    },

    selectImageFromWeb: function () {
        vex.dialog.prompt({
            message: 'Enter a web address of an image:',
            placeholder: 'Image url',
            callback: jQuery.proxy(this.imageFromUrlCallback, this)
        })
    },
    imageFromUrlCallback: function (url) {
        sl.log(url);
        this.testUrlForCors(url).then(jQuery.proxy(this.testedUrl, this))
    },

    directImageUpload: function (url) {
        var variables = {
            edit_form_data: this.getFormDataID(),
            block_id: this.blockID,
            file_path: url
        };
        jQuery.ajax({
            url: "/pf4/cms2/setImageWithPath",
            data: variables,
            success: jQuery.proxy(this.closeDialogAndUpdate, this),
            error: function () {
                sl.alert("There was an error setting this image");
            }
        });
    },


    selectBadgeFromLibrary: function () {
        var url = "/pf4/cms2_site/select_image_from_badge_list?mod=badge_list";//cms2_site/page/select_image_from_badge_list.pg4
        var objectName = "image" + this.blockID;
        window[objectName] = this;
        var variables = {};
        variables.object_name = objectName;
        variables.mod = "container-table-main";
        sl.addAjaxScope(variables);
        var dialogData = {
            url: url,
            contentClassName: "sl-dialog-wide",
            additonalData: variables,
            buttons: [
                jQuery.extend({}, vex.dialog.buttons.NO, {
                    className: 'vex-dialog-button-secondary vex-dialog-button vex-last',
                    text: 'Cancel',
                    click: jQuery.proxy(this.cancelHandler, this)
                })
            ],
        };
        var dialog = new Dialog(dialogData);
        dialog.createDialog().then(jQuery.proxy(this.initDialogListeners, this));
    },

    selectImageFromLibrary: function () {
        var url = "/pf4/cms2_site/select_image_from_list?mod=badge_list";//cms2_site/page/select_image_from_badge_list.pg4
        var dialogData = {
            url: url,
            contentClassName: "sl-dialog-wide",
            buttons: [
                jQuery.extend({}, vex.dialog.buttons.NO, {
                    className: 'vex-dialog-button-secondary vex-dialog-button vex-last',
                    text: 'Cancel',
                    click: jQuery.proxy(this.cancelHandler, this)
                })
            ],
        };
        var dialog = new Dialog(dialogData);
        dialog.createDialog().then(jQuery.proxy(this.initDialogListeners, this));
    },
    initDialogListeners: function (dialog) {
        jQuery(dialog).on({
            click: jQuery.proxy(this.dialogItemClickHandler, this)
        }, ".jsLibraryItem")

    },
    dialogItemClickHandler: function (event) {
        var target = (jQuery(event.target).hasClass("jsLibraryItem")) ? jQuery(event.target) : jQuery(event.target).closest(".jsLibraryItem");
        var url = target.data("url");
        this.directImageUpload(url)
    },


    testedUrl: function (test, url) {
        if (test == true) {
            var resize_options = {
                block_id: this.blockID,
                callback: jQuery.proxy(this.closeDialogAndUpdate, this),
                blockElement: this.blockElement,
                edit_form_data: this.getFormDataID(),
                title: "fromweb",
                embed_url: url,
                groupID: this.groupID
            };

            sl.log("url options", resize_options)
            var resizer = new ImageResizer(resize_options);
        }
        else {
            sl.alert("Sorry that image does not have Cross-Origin Resource Sharing (CORS) allowed.")
        }
    },
    testUrlForCors: function (url) {
        this.testDeferred = jQuery.Deferred();
        var img = new Image();
        img.crossOrigin = "";
        img.addEventListener("load", jQuery.proxy(this.testOnloadHandler, this, img)
        );
        img.onerror = function () {
            sl.alert("Sorry the server for that image does not have Cross-Origin Resource Sharing (CORS) allowed.");
            this.testDeferred.resolve(false);
        };
        img.src = url;
        return this.testDeferred;
    },
    testOnloadHandler: function (img) {
        var testCanvas = jQuery("").appendTo("body")
        var ctx = testCanvas[0].getContext("2d");
        ctx.drawImage(img, 0, 0);
        // This will fail if no CORS support, otherwise all OK
        var test;
        try {
            ctx.getImageData(0, 0, 10, 10);
            test = true;
        }
        catch (err) {
            test = false;
        }
        testCanvas.remove();
        this.testDeferred.resolve(test, img.src)
    }
});

BaseEditHandler.instanceCount = 0;


